INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (581, 'GRAND MAYAN NV', 1, 'The most exclusive and luxury concept in Bandera''s Bay, the resort is an impressive construction integrated into the natural landscape. Enjoy oceanfront restaurants and a snack bar, Rouge wine bar with a selection of wines from around the world, a lobby bar, disco, arcade, and pool bar. Enjoy the kayaks, electric boats, minitrain, and cyber cafe. Play at our 18-hole par 71 golf course designed by Jim Lipe, and visit Green Break restaurant. The pool is second-largest in Latin America. Kaana Spa offers massages and beauty treatments, a workout room, sauna, steam room, and Jacuzzi.', '2009-02-16 10:49:08', '2009-02-16 14:57:27', 'GMNV.jpg', 'image/jpeg', 50435, '2009-02-16 14:57:26', 'GMNVINSIDE.jpeg.jpg', 'image/jpeg', 35412, '2009-02-16 14:57:25', 'GMNVROOM1.jpg', 'image/jpeg', 41595, '2009-02-16 14:57:25', 'GMNVROOM2.jpg', 'image/jpeg', 57592, '2009-02-16 14:57:26', 'GMNVROOM4.jpg', 'image/jpeg', 105408, '2009-02-16 14:57:26');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (582, 'GRAND MAYAN LOS CABOS', 5, 'Located at the southern tip of the Baja Peninsula in Los Cabos, this enchanted retreat boasts an endless variety of activities with the perfect blend of intimacy and relaxation. It features a full-service, world-class health spa with beauty treatments, sauna, steam room, Jacuzzi, and fitness complex; clubhouse with nine-hole, par-35 golf course; large reflection pool that leads to a pristine golden beach; and fine international restaurants. This impressive construction is completely integrated into the natural landscape. This area is known as the marlin fishing capital of the world.', '2009-02-16 15:28:10', '2009-02-16 15:28:10', 'GMCABO.jpg', 'image/jpeg', 35885, '2009-02-16 15:28:10', 'GMCABO4.jpg', 'image/jpeg', 103025, '2009-02-16 15:28:08', 'GMCABOROOM1.jpg', 'image/jpeg', 64514, '2009-02-16 15:28:08', 'GMCABOROOM2.jpg', 'image/jpeg', 64267, '2009-02-16 15:28:09', 'GMCABOROOM3.jpg', 'image/jpeg', 68331, '2009-02-16 15:28:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (583, 'MAYAN PALACE CANCUN', 4, 'Hotel Cancun Palace offers you a world of tropical vacation pleasures. Bask on the powdery, white-sand beach; swim and snorkel in aquamarine waters; or try your luck at deep-sea fishing. Back on land, enjoy the resort''s tennis courts, gym, swimming pool, and restaurants, or you can tour the ancient Mayan ruins. Palace Resorts owns Golf Club Playacar, designed by Robert Van Haggen and The Golf Club at Moon Palace designed by Jack Nicklaus. There are special prices for RCI members. *UNLIMITED CALLS INCLUDED TO USA,CAN,MEX* Some restrictions apply. ', '2009-02-16 15:32:04', '2009-03-18 15:21:30', 'cancun2.jpg', 'image/jpeg', 48319, '2009-03-18 15:21:29', 'MPCANCUN1.jpg', 'image/jpeg', 82029, '2009-02-16 15:32:02', 'MPCANCUN2.jpg', 'image/jpeg', 27185, '2009-02-16 15:32:02', 'MPCANCUN3.jpg', 'image/jpeg', 75417, '2009-02-16 15:32:03', 'MPCANCUN4.jpg', 'image/jpeg', 32956, '2009-02-16 15:32:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (584, 'GRAND MAYAN ACAPULCO', 2, 'The Grand Mayan Acapulco provides an extraordinary setting for your vacation. This select resort, located along one-half mile of the breathtaking Playa Diamante, offers superlative guest rooms and suites with exclusive services and amenities. Enjoy spectacular views of the Pacific Ocean or our lush, tropical gardens.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (585, 'Mayan Palace Acapulco', 2, 'Mayan Palace Acapulco Golf Resort Enjoy the Pacific Breeze Welcome to the world-renowned city of Acapulco and the Mayan Palace Acapulco golf resort, where natural beauty mixes with the glamour of a bustling city. Enjoy the carefree attitude of Acapulco and the comfort of your stay at the Mayan Palace Acapulco golf resort. Explore Viejo Acapulco (Old Acapulco) to catch a glimpse of the city\'s unique soul, or choose from a variety of adventurous water activities. Snorkel, scuba dive, or just watch the famous Acapulco cliff divers of La Quebrada as they perform death defying feats.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (586, 'MAYAN PALACE NV', 1, 'The dream team of sun, sand and sea come together at the majestic Mayan Palace Nuevo Vallarta, a luxury vacation resort overlooking the beautiful Banderas Bay.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (587, 'Mayan Palace PV', 3, 'Puerto Vallarta could be crowned Mexico''s quintessential beach destination. Yet it''s actually much more, thanks to the city''s desire to re-cast its image as hip, cultured, and cosmopolitan. Readers of conde Nast magazine bestowed the city with the crown of "world''s most friendly" in 2002
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (611, 'The Park Regency', 46, 'Located in the Wasatch Range of the Rocky Mountains, the Park City area boasts three world-class ski resorts. At The Park Regency, guests will enjoy an indoor swimming pool, indoor spa, and easy access to an abundance of year-round amenities. Numerous shops, restaurants, and plenty of nightlife provide an excellent complement to the outdoor activities available', '2009-02-26 15:03:51', '2009-02-26 15:03:51', 'parkregency5.jpg', 'image/jpeg', 186204, '2009-02-26 15:03:50', 'parkregency2.jpg', 'image/jpeg', 20963, '2009-02-26 15:03:49', 'parkregency1.jpg', 'image/jpeg', 17173, '2009-02-26 15:03:49', 'parkregency3.jpg', 'image/jpeg', 156783, '2009-02-26 15:03:50', 'parkregency4.JPG', 'image/jpeg', 96259, '2009-02-26 15:03:50');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (588, 'MayanPalace PP', 14, 'Call it Rocky Point, Puerto Penasco or the new name of La Jolla del Cortes, the Mayan Palace is the best possible beach resort within driving distance of Tucson and Phoenix. You can rent a suite from us at the Mayan Palace. But hold it Penasco Fans!! This resort is so popular with Arizona folks that you will generally have to reserve at least 6 months in advance. If you contact us early you can rent a luxury suite for a week a the 5 star Mayan Palace Puerto Penasco. By renting from timeshare owners you can get very affordable rates See our Mayan Palace Discounts Home Page for more detailed information. You can be relaxing on the beach or golfing on one of the two excellent golf courses at the Mayan Palace Puerto Penasco resort in Rocky Point after a only 3 or 4 hour drive from Phoenix or Tucson! The Mayan Palace Puerto Penasco is located outside a shrimp fishing town on the Sea of Cortes. If you get tired of the Pacific view, look behind you at the beautiful mountains of El Pinacate, an ecological reserve.', '2009-02-19 12:26:21', '2009-04-07 11:47:07', 'MPPP.jpg', 'image/jpeg', 32278, '2009-02-19 12:26:21', 'MPPP1.jpg', 'image/jpeg', 57227, '2009-02-19 12:26:19', 'MPPP2.jpg', 'image/jpeg', 17320, '2009-02-19 12:26:20', 'MPPP3.jpg', 'image/jpeg', 33768, '2009-02-19 12:26:20', 'MPPP4.jpg', 'image/jpeg', 39703, '2009-02-19 12:26:20');
