INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (590, 'Mayan Palace Mazatlan', 32, 'Find a vacation unlike any other, when you visit the  \"Pearl of the Pacific.\" Mayan Palace Mazatlan resort is located in the fabulous city of Mazatlan, offering a beautiful glimpse of traditional Mexico. Vibrant nightlife, dining, and recreation on the water are all part of the charm of Mazatlan. During your stay at Mayan Palace Mazatlan resort, you can tour the  \"zona dorada,\" or  \"golden zone,\" to do some exploring and shopping. Enjoy a romantic dinner on the beach, or stroll through the tropical streets at night
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (591, 'Grand Mayan PP', 14, 'The Grand Mayan at San Jose del Cabo is a beautiful ?RCI Gold Crown Resort? was just completed in 2007.The Grand Mayan is located on the Southern Baja California Peninsula in the beautiful City of San Josedel Cabo, which is about 20 miles from Cabo San Lucas which forms part of a beautiful tourist corridor. Here you will be able to enjoy an enormous variety of services, the most beautiful natural scenery and incomparable beaches where you will be able to practice a great variety of sporting activities. The world''s marlin fishing capital and the captivating setting for The Grand MayaneLos Cabos Resort. World-class amenities available to our discriminating guests include a full-service luxury health spa and fitness complex with sauna, steam room and Jacuzzi; a nine-hole, par-35 golf course with clubhouse; a voluminous reflection pool that leads to a pristine beach; and superb restaurants providing exquisite international fare. Sleep (8) 6 Adults and (2) Children.', '2009-02-19 13:00:21', '2009-04-07 11:46:30', 'GMPP.jpg', 'image/jpeg', 580743, '2009-02-19 13:00:21', 'GMPP1.jpg', 'image/jpeg', 183847, '2009-02-19 13:00:19', 'GMPP2.jpg', 'image/jpeg', 10199, '2009-02-19 13:00:19', 'GMPP3.jpg', 'image/jpeg', 24196, '2009-02-19 13:00:20', 'GMPP4.jpg', 'image/jpeg', 18888, '2009-02-19 13:00:20');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (592, 'Grand Mayan Riviera Maya', 70, 'Experience tranquility combined with adventure on Caribbean vacations at the Grand Mayan Riviera Maya resort, twenty-one miles from Cancun International Airport. Near the tip of the Yucatan Peninsula, we are one of the best Mexico resorts, boasting dazzling beaches, meandering pools, beautiful palm-covered landscapes, sparkling lakes and a lagoon.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (600, 'The Fairways of Palm Aire', 61, 'The Fairways of Palm Aire provides a wide array of vacation activities. The resort is best known for its championship golf courses. A full-service spa and fitness center are on site to offer you the ultimate in pampering and self-indulgence. In addition, the resort offers shuttle service to the beach twice each week. Tennis courts and three outdoor pools complete the on-site amenities, and directly across the street is the Pompano Harness Track. World-class shopping and dining await you in nearby "Fort Lauderdale." With all this area has to offer, you won''t know where to begin.', '2009-02-25 12:16:07', '2009-02-25 12:16:07', 'thefairways1.jpg', 'image/jpeg', 52764, '2009-02-25 12:16:07', 'fairways5.jpg', 'image/jpeg', 15586, '2009-02-25 12:16:05', 'fairways2.jpg', 'image/jpeg', 24339, '2009-02-25 12:16:06', 'fairways3.jpg', 'image/jpeg', 398141, '2009-02-25 12:16:06', 'fairways4.jpg', 'image/jpeg', 25486, '2009-02-25 12:16:07');
