INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (594, 'Occidental Allegro Nuevo Vallarta', 1, 'Cradled by an exquisite stretch of beach on the famed Bay of Banderas, tranquil deep blue water and the tropical vegetation of the Mexican Pacific, the all-inclusive Allegro Nuevo Vallarta offers guests an endless blend of fun, relaxation, romance and attentive service. Located a short 15-minute ride from the airport and 25 minutes from Puerto Vallarta''s charming downtown, the Allegro Nuevo Vallarta features a host of recreational water and land activities, three specialty restaurants, three bars/lounges, daily activities program, nightly entertainment, and a Kids Club.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (595, 'Playa Del Sol Grand', 1, 'This new five-star, full service resort offers world class sun, sand and surf in a tranquil atmosphere just 15 minutes after your arrival at the Puerto Vallarta International Airport.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (596, ' Orange Lake Resort & Country Club, Orlando', 61, 'Enjoy your vacation in style by staying in one of Orange Lake''s award-winning villas. You''ll have access to 1,100-acres of limitless activities including two pool complexes, an 80-acre lake, waterskiing, tennis courts, and beach volleyball. Known for its golf, Orange Lake also offers.World-class golf courses that wind through the property, attracting players from down the street and across the world.The diversity of our championship golf courses enables you to select the type of course that best suits your playing style and skill level - with professional instruction available to help you reach the top of your game. You can play championship golf here all day on 90 holes, with two courses lighted for nighttime play. Golfers at all playing levels will enjoy the challenge that only Orange Lake Golf can deliver. There are many planned activities and nightly entertainment. Orange Lake is five minutes from Walt Disney World(R) and close to other major attractions. Walk through the front door of your stylishly appointed villa and you''ll discover a vacation home that offers both luxury and ample space. From the award-winning decor to the fully-equipped kitchen, our villas and suites let you enjoy your vacation in the style and convenience to which you are accustomed. Something for Everyone Beautiful accommodations are spacious for family comfort. Beautiful lakes provide peaceful views and cool pools offer refreshing fun. It''s Orange Lake''s mission to always exceed the expectations of our owners and guests. That''s why 76,000 owners have made Orange Lake their timeshare vacation home. And why it''s chosen as a vacation destination by so many RCI exchange members and those seeking a weekend getaway. To our owners and guests welcome to Orange Lake today!', '2009-02-24 15:01:18', '2009-02-24 15:01:18', 'oranglake1.jpg', 'image/jpeg', 108824, '2009-02-24 15:01:18', 'orangelake2.JPG', 'image/jpeg', 32446, '2009-02-24 15:01:16', 'orangelake3.jpg', 'image/jpeg', 53420, '2009-02-24 15:01:17', 'orangelake4.jpg', 'image/jpeg', 23616, '2009-02-24 15:01:17', 'orangelake5.jpg', 'image/jpeg', 60533, '2009-02-24 15:01:18');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (597, 'Villas Vallarta', 3, 'Enjoy the beauty and sunshine of Puerto Vallarta: Move to a leisurely tropical beat by day and an exciting Latin rhythm at night. Gloden Hotel & Fitness Resort Glorias boasts spa facilities, restaurants and pool all near a glistening beach and a famous shopping plaza.', '2009-02-25 11:49:31', '2009-02-26 16:17:58', 'villasvalltera3.jpg', 'image/jpeg', 20911, '2009-02-25 11:49:31', 'villasvalltera1.jpg', 'image/jpeg', 13540, '2009-02-25 11:49:29', 'villasvalltera2.jpg', 'image/jpeg', 10225, '2009-02-25 11:49:30', 'villasvalltera4.jpg', 'image/jpeg', 110532, '2009-02-25 11:49:30', 'villasvalltera5.jpg', 'image/jpeg', 29249, '2009-02-25 11:49:30');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (598, 'Gatlinburg Town Square', 63, 'The Great Smoky Mountains offer a wealth of vacation fun, and you''ll find it all at Gatlinburg Town Square Resort. The attractions of Gatlinburg are just a block away, or take a drive and explore the nearby mountains. Numerous trails and paths can be found easily. The resort offers an indoor and outdoor pool for year-round swimming, an exercise room, and a hot tub. The resort also offers underground parking and on-site activities and is located near dinner theaters and other local attractions. Dollywood is only eight miles away.', '2009-02-25 11:59:38', '2009-02-25 11:59:38', 'gatlin5.jpg', 'image/jpeg', 38426, '2009-02-25 11:59:38', 'gatlin1.jpg', 'image/jpeg', 11632, '2009-02-25 11:59:37', 'gatlin3.jpg', 'image/jpeg', 21719, '2009-02-25 11:59:37', 'gatlin2.jpg', 'image/jpeg', 13837, '2009-02-25 11:59:37', 'gatlin4.jpg', 'image/jpeg', 11955, '2009-02-25 11:59:38');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (599, 'Villas Del Palmar', 3, 'Let the Villa del Mar and the Villa del Palmar resorts be your "home away from home" in Puerto Vallarta. Conveniently located in the central hotel area and 10 minutes by taxi to downtown Puerto Vallarta. Choose from spacious studio, one bedroom and two-bedroom suites, all with fully equipped kitchenettes and balconies or terraces. Laze by our pool, walk miles of golden beach or take part in an assortment of resort activities including tennis, water aerobics, pool games and an abundance of water sports! Enjoy outstanding restaurants and join our Entertainment Staff for fantastic evening entertainment', '2009-02-25 12:06:03', '2009-02-25 12:09:49', 'villadelpalmar1.jpg', 'image/jpeg', 21855, '2009-02-25 12:06:03', 'villadelpalmar1.jpg', 'image/jpeg', 163184, '2009-02-25 12:09:47', 'villadelpalmar2.jpg', 'image/jpeg', 146712, '2009-02-25 12:09:48', 'villadelpalmar4.JPG', 'image/jpeg', 161030, '2009-02-25 12:09:48', 'villadelpalmar5.jpg', 'image/jpeg', 116960, '2009-02-25 12:09:49');
