INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (601, 'Playa Grande Resort Los Cabos', 5, 'Blessed with nearly year-round sunshine, Southern Baja''s rare and remote beauty is yours to explore at Playa Grande Resort. Cabo San Lucas is enjoying increasing popularity. Pacific waters as well as a beach are just a step away and offer the best in deep-sea fishing. Resort amenities include a swimming pool and restaurant. This resort has a unique Mexican hacienda style. Where the Pacific meets the sands of Cabo San Lucas, you''ll find Baja California''s most exclusive and private beach, home of Playa Grande Resort. The charm of Playa Grande''s Mexican hacienda style architecture and landscape enhances its location on eight acres of Solmar Beach, with 1,000 feet of beach front. The beauty and intimacy of Solmar Beach interacts tastefully with the grandest and most breathtaking Resort in Cabo San Lucas ... all within a few minute walking to downtown of Cabo. Playa Grande Resort, basking in the warmth and tradition of Cabo San Lucas is the ideal place for relax and enjoy your dream vacation. These luxurious condominiums are designed for the most discriminating vacationer, retaining the comforting allure and gracefulness of the architectural culture of Mexico.', '2009-02-25 12:22:44', '2009-02-25 12:22:44', 'playagrande1.jpg', 'image/jpeg', 97712, '2009-02-25 12:22:43', 'playagrande5.jpg', 'image/jpeg', 68546, '2009-02-25 12:22:42', 'playagrande2.jpg', 'image/jpeg', 90387, '2009-02-25 12:22:42', 'playagrande4.jpg', 'image/jpeg', 71979, '2009-02-25 12:22:43', 'playagrande3.jpg', 'image/jpeg', 52623, '2009-02-25 12:22:43');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (602, 'Orlando''s Sunshine Resort', 61, 'Orlando''s Sunshine Resort II boasts an on- site swimming pool, tennis court, kiddie pool, shuffleboard court, outdoor spa, sauna, and cookout area. Within 15 miles of the resort are famous family attractions such as Walt Disney World(R), Universal Studios, and SeaWorld. After a day of play, return to the resort for a relaxing stint in the on-site whirlpool.', '2009-02-25 12:31:37', '2009-02-25 12:31:37', 'orlandosunshine1.jpg', 'image/jpeg', 63599, '2009-02-25 12:31:37', 'orlandosunshine2.jpg', 'image/jpeg', 83217, '2009-02-25 12:31:35', 'orlandosunshine3.jpg', 'image/jpeg', 15146, '2009-02-25 12:31:36', 'orlandosunshine5.jpg', 'image/jpeg', 15197, '2009-02-25 12:31:36', 'orlandosunshine4.jpg', 'image/jpeg', 12842, '2009-02-25 12:31:36');
