INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (604, 'Pueblo Bonito Resort at Emerald Bay', 32, 'Mazatlan, a city like no other in Mexico, is nestled on a natural bay of the Pacific Ocean and on a scenic peninsula that boasts the largest main crescent-shaped boulevard as well as a wide variety of outdoor tours, activities and shopping areas. Pueblo Bonito at Emerald Bay is right on the beach where you can enjoy all kinds of water sports. The interior decor is designed in the casual elegance of the old Mazatlan. Two huge courtyards surround free-form swimming pools so you can relax and enjoy Mazatlan''s sunsets. When dining, Mazatlan has a large selection, but seafood is still the favorite.', '2009-02-25 12:49:30', '2009-02-25 12:49:30', 'pbemlerbay1.jpg', 'image/jpeg', 25330, '2009-02-25 12:49:30', 'pbemlerbay3.jpg', 'image/jpeg', 63568, '2009-02-25 12:49:28', 'pbemlerbay4.jpg', 'image/jpeg', 52362, '2009-02-25 12:49:29', 'pbemlerbay2.jpg', 'image/jpeg', 30023, '2009-02-25 12:49:29', 'pbemlerbay4.jpg', 'image/jpeg', 52362, '2009-02-25 12:49:29');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (605, 'Oak Plantation', 61, 'Nestled on 16 forested acres only eight miles from Walt Disney World(R), Oak Plantation offers a secluded getaway in the heart of central Florida. The resort features cozy one- and two-bedroom villas, which surround a scenic lake with a lighted fountain. Each villa has a fully equipped kitchen. Amenities include gated access, a free-form pool complex, lighted tennis court and basketball court, games room, car care area, fitness center, and picnic spots. Play golf close by or walk to shopping and restaurants.', '2009-02-25 13:02:26', '2009-02-25 13:04:12', 'oakplantation.jpg', 'image/jpeg', 40561, '2009-02-25 13:04:12', 'oakplan1.jpg', 'image/jpeg', 43851, '2009-02-25 13:02:24', 'oakplan3.jpg', 'image/jpeg', 12311, '2009-02-25 13:02:25', 'oakplan2.jpg', 'image/jpeg', 35433, '2009-02-25 13:02:25', 'oakplan5.jpg', 'image/jpeg', 75336, '2009-02-25 13:02:26');
