INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (617, 'Carraige Hills', 49, 'All-season luxury retreat in scenic Horseshoe Valley. Enjoy year-round Carriage House Recreation Centre which includes a large heated indoor/outdoor pool, fitness facilities, indoor and outdoor hot tubs, family programs, mountain biking, volleyball, basketball net and surrounding hiking trails. Winter amenities include downhill and cross-country skiing, snowboarding, snowtubing, skating etc. at the main village of Carriage Hills & Horseshoe Resorts. May to October enjoy golf and lessons on 2 championship courses, as well as hiking, horseshoes and shuffleboard.', '2009-02-27 12:47:13', '2009-02-27 12:47:13', 'carraigehills1.jpg', 'image/jpeg', 12520, '2009-02-27 12:47:12', 'carraigehills2.jpg', 'image/jpeg', 13033, '2009-02-27 12:47:11', 'carraigehills4.jpg', 'image/jpeg', 19576, '2009-02-27 12:47:11', 'carraigehills5.jpg', 'image/jpeg', 20618, '2009-02-27 12:47:12', 'carriagehills3.jpg', 'image/jpeg', 16746, '2009-02-27 12:47:12');
