INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (624, 'Hilton Grand Vacations Club at the Las Vegas Hilton', 37, 'Ideally located adjacent to The Las Vegas Hilton, the Hilton Grand Vacation Club at the Las Vegas Hilton provides world-class accommodations. Enjoy beautiful studios and one- and two-bedroom suites along with a variety of on-site coveniences including a swimming pool, spa, and health club. The resort offers immediate proximity to the extensive restaurants and amenities of the hotel and is close to the excitement of the nearby Las Vegas Strip.', '2009-03-04 12:05:38', '2009-03-04 12:05:38', 'hglasvegas1.jpg', 'image/jpeg', 17143, '2009-03-04 12:05:38', 'hglasvegas2.jpg', 'image/jpeg', 11803, '2009-03-04 12:05:37', 'hglasvegas3.jpg', 'image/jpeg', 2722, '2009-03-04 12:05:37', 'hglasvegas4.jpg', 'image/jpeg', 3174, '2009-03-04 12:05:37', 'hglasvegas5.jpg', 'image/jpeg', 5174, '2009-03-04 12:05:38');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (625, 'Village Caribe Tennis, Golf & Beach Resort', 41, 'From windsurfing to golfing to sunbathing, the Dominican Republic''s northern shore provides vacation pleasures to fill each moment. Situated one mile from the beach in the Playa Dorada resort complex, the Village Caraibe Tennis, Golf & Beach Resort is convenient to all this tropical playground has to offer. Lounge by the resort''s outdoor pool or visit the beaches along the Atlantic Ocean, where you can enjoy an endless list of water activities. An 18-hole Robert Trent Jones golf course and outdoor tennis courts are all on site.', '2009-03-04 12:14:32', '2009-03-04 12:14:32', 'villagecaribe1.jpg', 'image/jpeg', 16651, '2009-03-04 12:14:31', 'villagecaribe2.jpg', 'image/jpeg', 11748, '2009-03-04 12:14:30', 'villagecaribe2.jpg', 'image/jpeg', 11748, '2009-03-04 12:14:30', 'villagecaribe3.jpg', 'image/jpeg', 4962, '2009-03-04 12:14:31', 'villagecaribe4.jpg', 'image/jpeg', 43008, '2009-03-04 12:14:31');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (626, 'Hacienda Encantada', 5, 'Hacienda Encantada Resort & Spa comprises studios, one- and two-bedroom accommodations, and a penthouse. Some units feature balconies with breathtaking views of the famous Arch of Cabo San Lucas. The resort is designed in a contemporary Mexican hacienda style, with amenities ranging from a beach-side restaurant, seven swimming pools, tennis courts, and whirlpools to a restaurant, bar, full-service spa, and shopping center', '2009-03-04 12:19:37', '2009-03-04 12:19:37', 'haciendaencantada1.jpg', 'image/jpeg', 24440, '2009-03-04 12:19:37', 'haciendaencantada2.jpg', 'image/jpeg', 26653, '2009-03-04 12:19:35', 'haciendaencantada3.jpg', 'image/jpeg', 3822, '2009-03-04 12:19:35', 'haciendaencantada4.jpg', 'image/jpeg', 9372, '2009-03-04 12:19:36', 'haciendaencantada5.jpg', 'image/jpeg', 4969, '2009-03-04 12:19:36');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (627, 'The wind in the pines', 26, 'Winter or summer, Wind in the Pines offers recreational diversions for the entire family. Among the most popular activites are snowskiing, hiking, and fishing. Its prime location in the Berksires of western Massachusetts allows for many sight-see-ing opportunities, including Tanglewood, summer home of the Boston Symphony Orchestra; the Norman Rockwell Museum; and the shops and galleries in Stockbridge.', '2009-03-04 12:24:31', '2009-03-04 12:24:31', 'winddddssspines.jpg', 'image/jpeg', 13839, '2009-03-04 12:24:30', 'winddddssspines1.jpg', 'image/jpeg', 15494, '2009-03-04 12:24:28', 'winddddssspines2.jpg', 'image/jpeg', 13008, '2009-03-04 12:24:29', 'winddddssspines3.jpg', 'image/jpeg', 8781, '2009-03-04 12:24:29', 'winddddssspines3.jpg', 'image/jpeg', 8781, '2009-03-04 12:24:30');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (628, 'Westin Regina Resort Puerto Vallarta', 3, 'The Westin Regina Resort Puerto Vallarta, ranked on the Conde Nast Golden List as one of the best places to stay in the world. This spectacular oceanfront resort, set on 21 lush acres, combines the ambience of colonial Mexico with the most sophisticated services and amenities. Guests can let the rest of the world slip away at any of our four unique pools surrounded by 600 palm trees, magnificent gardens and 879 feet of Pacific Ocean beach. Located in one of the most exclusive Mexican resort complexes, Marina Vallarta is only 5 minutes from airport and 12 minutes from downtown. Modern structures with fine Mexican touches of hand-made mosaic stone floors and bright vibrant colors. The renowned Mexican Architect Javier Sordo Madaleno designed Westin''s grounds and buildings to take the best advantage of the beach and 600 palm trees still standing from the days of a coconut plantation. It is a truly magnificent place to stay and experience', '2009-03-04 12:29:25', '2009-03-04 12:29:25', 'westinreginapv1.jpg', 'image/jpeg', 22815, '2009-03-04 12:29:25', 'westinreginapv2.jpeg', 'image/jpeg', 29168, '2009-03-04 12:29:23', 'westinreginapv3.jpg', 'image/jpeg', 37179, '2009-03-04 12:29:23', 'westinreginapv4.jpg', 'image/jpeg', 28660, '2009-03-04 12:29:24', 'westinreginapv5.jpg', 'image/jpeg', 20722, '2009-03-04 12:29:24');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (629, 'Paradise Village Beach Resort & Spa', 1, 'Paradise Village is situated in a prime beachfront location in the Nuevo Vallarta development, five miles north of the Puerto Vallarta International Airport. All units feature spectacular views of Puerto Vallarta, beautiful Banderas Bay, marina, and mountains. On-site are a full-service, world-class health spa featuring European wellness programs and treatments; multiple dining experiences; nightclub; marina; and virtually all water sports. The complex''s unusual Mayan theme is reflected in the swimming pools, which have caves and waterslides. The Paradise Village 18-hole championship golf course is just minutes away.', '2009-03-04 12:39:24', '2009-03-04 12:39:24', 'paridisevillage1.jpg', 'image/jpeg', 24584, '2009-03-04 12:39:23', 'paridisevillage2.jpg', 'image/jpeg', 7065, '2009-03-04 12:39:22', 'paridisevillage2.jpg', 'image/jpeg', 7065, '2009-03-04 12:39:22', 'paridisevillage3.jpg', 'image/jpeg', 8505, '2009-03-04 12:39:23', 'paridisevillage4.jpg', 'image/jpeg', 43458, '2009-03-04 12:39:23');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (630, 'Kona Coast Resort,', 28, 'Amidst the panoramic backdrop of Hawaii''s Kona Coast, a vacation unlike any other awaits you. The Kona Coast Resort offers guests the chance to enjoy all the best of the Big Island- its natural beauty, fascinating heritage, and endless recreational activities are all within easy reach. The resort itself offers superb recreational activities, such as tennis, volleyball, and swimming, as well as fine dining and luxurious accommodations . And the Resort is just a starting point for your adventures- from water-skiing on crystalline tropical waters, to snow-skiing on breathtaking Mauna Kea, the geographical diversity found within easy distance of the Resort is as remarkable as it is exhilarating. Come visit the Kona Coast Resort and see for yourself why the possibilities are truly endless.', '2009-03-04 12:42:31', '2009-03-04 12:42:31', 'konacoast1.jpg', 'image/jpeg', 46328, '2009-03-04 12:42:30', 'konacoast2.JPG', 'image/jpeg', 36088, '2009-03-04 12:42:29', 'konacoast3.JPG', 'image/jpeg', 22335, '2009-03-04 12:42:29', 'konacoast4.JPG', 'image/jpeg', 25434, '2009-03-04 12:42:29', 'konacoast5.jpg', 'image/jpeg', 34515, '2009-03-04 12:42:30');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (631, 'The Cliffs Club', 28, 'The Cliffs Club Phase I & Phase III is located in Princeville, on Kauai''s north- ern coast. At the resort, you''ll find an outdoor pool, a spa for adults, a coed sauna, four tennis courts, putting green, horseshoes, and barbecue grills. One of the major attractions is the 45-hole Robert Trent Jones Jr. golf course. The Prince course is the only par- 6 layout in Hawaii. An activities desk is available to schedule off-site activities. Close by at Hanalei Bay, enjoy windsurfing, scuba diving, and snorkeling. Plan to attend a mini-introduction to windsurfing or scuba diving.', '2009-03-04 12:47:56', '2009-03-04 12:47:56', 'thecliffsclub1.jpg', 'image/jpeg', 14481, '2009-03-04 12:47:56', 'thecliffsclub2.jpg', 'image/jpeg', 11588, '2009-03-04 12:47:54', 'thecliffsclub3.jpg', 'image/jpeg', 31010, '2009-03-04 12:47:55', 'thecliffsclub4.jpg', 'image/jpeg', 24137, '2009-03-04 12:47:55', 'thecliffsclub5.jpg', 'image/jpeg', 17055, '2009-03-04 12:47:55');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (632, 'Enchanted Isle Resort', 61, 'Just south of Fort Lauderdale, Enchanted Isle Resort offers a perfect base for exploring the attractions of southeastern Florida. Water sports are popular along Hollywood''s beaches. Deep-sea charters can be arranged at various locations close by, and many other activities are either on site or nearby. Golf, tennis, and bicycling can be enjoyed year-round. To complete your vacation, take advantage of the many restaurants, shopping malls, and nightclubs in the area.', '2009-03-04 12:57:33', '2009-03-04 12:57:33', 'enchatedisle1.jpg', 'image/jpeg', 56874, '2009-03-04 12:57:33', 'enchatedisle2.jpg', 'image/jpeg', 13493, '2009-03-04 12:57:31', 'enchatedisle3.jpg', 'image/jpeg', 13493, '2009-03-04 12:57:32', 'enchatedisle4.jpg', 'image/jpeg', 7883, '2009-03-04 12:57:32', 'enchatedisle5.jpg', 'image/jpeg', 16003, '2009-03-04 12:57:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (633, 'Wyndham Smoky Mountains', 63, 'Wyndham smoky mountains is the ideal family vacation spot! Enjoy the beauty of the Great Smoky Mountains. Hikers can take advantage of over 800 miles of trails. Sevierville is home to the area''s premier outlet shopping. Pigeon Forge boasts a variety of dinner theatres, mini golf, shopping and Dollywood! The quaint town of Gatlinburg is home to Ober-Gatlinburg, the Great Smoky Mountains Arts and Crafts community and a 1 mile pedestrian parkway lined with shops and restaurants. Whether you are looking for a relaxing day of peace and quiet, or fun and excitement, you''ll find that this resort has is all !', '2009-03-04 13:01:23', '2009-03-04 13:01:23', 'wyndhamsmokymoutains1.jpg', 'image/jpeg', 13558, '2009-03-04 13:01:22', 'wyndhamsmokymoutains2.jpg', 'image/jpeg', 13368, '2009-03-04 13:01:21', 'wyndhamsmokymoutains3.jpg', 'image/jpeg', 3368, '2009-03-04 13:01:21', 'wyndhamsmokymoutains4.jpg', 'image/jpeg', 37717, '2009-03-04 13:01:22', 'wyndhamsmokymoutains5.jpg', 'image/jpeg', 34509, '2009-03-04 13:01:22');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (634, 'Table Rock Resort', 15, 'Table Rock Resort is located in the foothills of the Blue Ridge Mountains in northern Pickens County South Carolina. Available on site is a picturesque golf course surrounded by mountain streams and beautiful views. Other amenities are horse back riding, tennis and a swimming pool open seasonal . The resort is just off of scenic Hwy. 11, which has several large lakes and state parks for the outdoor enthusiast. Come relax and enjoy the quite and serenity of the mountain and lake country of upstate South Carolina. ', '2009-03-04 13:07:53', '2009-03-04 13:07:53', 'tablerock1.JPG', 'image/jpeg', 29387, '2009-03-04 13:07:53', 'tablerock1.JPG', 'image/jpeg', 29387, '2009-03-04 13:07:52', 'tablerock1.JPG', 'image/jpeg', 29387, '2009-03-04 13:07:52', 'tablerock2.jpg', 'image/jpeg', 49517, '2009-03-04 13:07:52', 'tablerock2.jpg', 'image/jpeg', 49517, '2009-03-04 13:07:53');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (635, 'Whiski Jack at Lake Placid Lodge', 52, 'Enjoy the spectacular mountain setting of Whistler Vacation Club at Lake placid Lodge. The resort is located across from the gondola at the base of Whistler Mountain in Whistler Creekside. The many shops and restaurants of Whistler Village are located three miles away. An automobile is advisable, although public transit is right outside your door. Activities nearby include world class skiing, three championship golf courses, and a network of trails for hiking and biking.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (636, 'Vacation Villas at Bonaventure', 61, 'Located in the famous South Florida Bonaventure Spa, Tennis, and Golf Community, the luxurious Vacation Village at Bonaventure features spacious units. Conveniently located within the resort area are restaurants, family entertainment, and shopping. Horseback riding, tennis, golf, swimming, and racquetball are just some of the additional activities available, and nightlife is 25 minutes away. Fort Lauderdale Beach is 25 miles away.', '2009-03-04 13:18:44', '2009-03-04 13:18:44', 'VACVILLASBONADV1.JPG', 'image/jpeg', 73270, '2009-03-04 13:18:43', 'VACVILLASBONADV2.JPG', 'image/jpeg', 42304, '2009-03-04 13:18:42', 'VACVILLASBONADV3.jpg', 'image/jpeg', 4210, '2009-03-04 13:18:42', 'VACVILLASBONADV4.jpg', 'image/jpeg', 21343, '2009-03-04 13:18:42', 'VACVILLASBONADV5.jpg', 'image/jpeg', 21643, '2009-03-04 13:18:43');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (637, 'Club Casa Maya', 4, 'Cancun, considered paradise by Mayan kings, is home to Club Casa Maya. As visitors walk along dazzling beaches and skin dive in tranquil turquoise lagoons, they will realize they''ve encountered a rare treasure. Worthy of its idyllic setting, the beachfront resort boasts three outdoor swimming pools, tennis courts, and marina club services to help guests enjoy the beautiful Mexican Caribbean.', '2009-03-04 13:22:39', '2009-03-04 13:22:39', 'clubcasamaya1.jpg', 'image/jpeg', 15646, '2009-03-04 13:22:38', 'clubcasamaya2.jpg', 'image/jpeg', 12935, '2009-03-04 13:22:37', 'clubcasamaya3.jpg', 'image/jpeg', 4751, '2009-03-04 13:22:37', 'clubcasamaya4.jpg', 'image/jpeg', 79641, '2009-03-04 13:22:38', 'clubcasamaya5.jpg', 'image/jpeg', 35678, '2009-03-04 13:22:38');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (638, 'Pueblo Bonito Resort at Sunset Beach', 5, 'Blessed with nearly year-round sunshine, Southern Baja''s rare and remote beauty is yours to explore at Pueblo Bonito Sunset Beach. Cabo San Lucas is enjoying an increase in popularity. Pacific waters as well as a beach are just steps away and offer the best in deep-sea fishing. Resort amenities include a pool, gym, and restaurant. This resort has a traditional Mexican hacienda style.', '2009-03-04 13:33:12', '2009-03-04 13:33:12', 'pueblobonitasunset1.jpg', 'image/jpeg', 33298, '2009-03-04 13:33:11', 'pueblobonitasunset2.jpg', 'image/jpeg', 54240, '2009-03-04 13:33:10', 'pueblobonitasunset3.jpg', 'image/jpeg', 3376, '2009-03-04 13:33:10', 'pueblobonitasunset4.jpg', 'image/jpeg', 4383, '2009-03-04 13:33:10', 'pueblobonitasunset5.jpg', 'image/jpeg', 25537, '2009-03-04 13:33:11');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (639, 'Marriott''s Grand Chateau', 37, 'Marriott''s Grand Chateau is located at the center of the excitement on the legendary Las Vegas Strip. Throughout the resort, experience casual elegance reminiscent of the timeless mansions of southern France. Beautifully furnished one-, two-, and three-bedroom villas feature a full kitchen, an oversized soaking tub, 42-inch plasma display television, separate living and dining areas, and washer and dryer. After a night on the town, unwind at the rooftop pool with adjacent bar, while enjoying a breathtaking desert sunset, or the brilliant lights and sensational sounds of the city below', '2009-03-05 14:08:56', '2009-03-05 14:08:56', 'marriotgrandchetua1.jpg', 'image/jpeg', 4435, '2009-03-05 14:08:56', 'marriotgrandchetua2.jpg', 'image/jpeg', 2954, '2009-03-05 14:08:55', 'marriotgrandchetua3.jpg', 'image/jpeg', 2549, '2009-03-05 14:08:55', 'marriotgrandchetua4.jpg', 'image/jpeg', 3949, '2009-03-05 14:08:55', 'marriotgrandchetua5.jpg', 'image/jpeg', 3944, '2009-03-05 14:08:56');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (640, 'Hollywood Beach Tower', 61, 'Hollywood Beach Tower, on Florida''s southeastern coast between Fort Lauderdale and Miami, puts you in a great location to enjoy the shops, restaurants, museums, and nightspots of both cities, as well as the beautiful, palm-lined beaches for which Hollywood is known. If you long for lazy days of sunning and swimming, you don''t need to go further than you doorstep. The resort is located on the beach. Tennis facilities and several golf courses are in the area.', '2009-03-05 14:14:51', '2009-03-05 14:14:51', 'hollywood1.jpg', 'image/jpeg', 4050, '2009-03-05 14:14:50', 'hollywood2.jpg', 'image/jpeg', 3548, '2009-03-05 14:14:49', 'hollywood3.jpg', 'image/jpeg', 2913, '2009-03-05 14:14:49', 'hollywood4.jpg', 'image/jpeg', 2313, '2009-03-05 14:14:50', 'hollywood5.jpg', 'image/jpeg', 3803, '2009-03-05 14:14:50');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (641, 'Silver Beach Club Resort', 61, 'Located on a wide, sandy beach, Silver Beach Club Resort features a heated, Olympic-style outdoor pool, a hot tub, children''s pool, games room, and a rooftop sun deck. Shuffleboard and tennis courts are within a mile, and marinas close by offer boat rentals and deep-sea fishing charters. The Halifax/Daytona Yacht Club and the Daytona Cubs baseball stadium are less than a mile away. Orlando attractions--Walt Disney World (R), SeaWorld, and Universal Studios--are about an hour''s drive away. Kennedy Space Center and historic St. Augustine are close by and make for unique day-trips.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (642, 'Grand Pacific Palisades', 20, 'Grand Pacific Palisades Resort rises above Carlsbad''s magnificent Flower Fields, in bloom from mid-March through April, and is located less than a mile from beautiful beaches. Guests will enjoy a discount off admission tickets to LEGOLAND CA., a 128- acre family theme park with over 3 million LEGO blocks for creative play for both young and old. Disneyland and Sea World are a short car drive away, and bus tours of Hollywood or Tijuana can be booked through the Concierge. Guests will also enjoy the extensive in-house and local activities available or can simply relax in the luxurious atmosphere of the resort', '2009-03-05 14:39:50', '2009-03-05 14:39:50', 'grandpacifics1.jpg', 'image/jpeg', 3093, '2009-03-05 14:39:49', 'grandpacifics2.jpg', 'image/jpeg', 4365, '2009-03-05 14:39:48', 'grandpacifics3.jpg', 'image/jpeg', 4924, '2009-03-05 14:39:49', 'grandpacifics4.jpg', 'image/jpeg', 3583, '2009-03-05 14:39:49', 'grandpacifics5.jpg', 'image/jpeg', 4243, '2009-03-05 14:39:49');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (643, 'Royal Hoilday Club Cancun', 4, 'The exquisite beaches and aquamarine seas of Cancun provide a fitting backdrop for RHC/Piramides Cancun Beach Resort. Every amenity you could desire is provided at this beachfront resort. Tennis courts, two pools, a health club nearby, and a restaurant are just some of the on-site features.', '2009-03-05 14:50:49', '2009-03-05 14:50:49', 'rhccancun1.jpg', 'image/jpeg', 3382, '2009-03-05 14:50:49', 'RHCancun2.jpg', 'image/jpeg', 2168, '2009-03-05 14:50:48', 'rhccancun3.jpg', 'image/jpeg', 3825, '2009-03-05 14:50:48', 'rhccancun4.jpg', 'image/jpeg', 3479, '2009-03-05 14:50:48', 'rhccancun5.jpg', 'image/jpeg', 3059, '2009-03-05 14:50:49');
