INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (646, 'Cabo Villas Beach Resort', 5, 'Cabo Villas Beach Resort is the new phase two addition of the existing Cabo Villas Resort. The new amenities and luxury designed villas unify both properties into one large world class Beachfront Resort.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (647, 'Highlands Inn, A Hyatt Hotel', 20, 'Experience the centerpiece of Carmel hotels. Highlands Inn is a picturesque retreat and longtime favorite among honeymooners, celebrities, and nature enthusiasts alike. When choosing among Carmel hotels, choose Highlands Inn for unsurpassed service and comfort.', '2009-03-11 12:06:10', '2009-03-11 12:06:10', 'CARMELH1.jpg', 'image/jpeg', 96267, '2009-03-11 12:06:10', 'CARMELH2.jpg', 'image/jpeg', 83400, '2009-03-11 12:06:08', 'CARMELH3.jpg', 'image/jpeg', 43336, '2009-03-11 12:06:09', 'CARMELH4.jpg', 'image/jpeg', 59040, '2009-03-11 12:06:09', 'CARMELH5.jpg', 'image/jpeg', 68426, '2009-03-11 12:06:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (648, 'Aspen Townhomes', 54, 'Aspen Townhomes is nestled in the Beartooth Range of the Rocky Mountains. The 68-mile Beartooth Highway, open late May through October, winds through the Rockies to Yellowstone National Park, our nation''s very first park. Summer activities include golfing on the resort''s 18-hole course, fishing, tennis, white-water rafting, and horseback riding. The city of Red Lodge offers live entertainment and dancing, as well as fine dining and unique shops. In winter, the area receives 100 inches of ski-perfect snow. The Red Lodge Mountain Ski Resort is visible from the main parking lot , and offers more than 25 miles of trails for downhill skiing and snowboarding. Wildlife abounds in this area, and nature enthusiasts will be delighted. This is a great vacation destination any time of the year.', '2009-03-12 12:06:04', '2009-03-12 12:06:04', 'Aspen-Townhomes-1.jpg', 'image/jpeg', 18959, '2009-03-12 12:06:04', 'Aspen-Townhomes-3.jpg', 'image/jpeg', 15153, '2009-03-12 12:06:02', 'Aspen-Townhomes-4.jpg', 'image/jpeg', 14863, '2009-03-12 12:06:02', 'Aspen-Townhomes-6.jpg', 'image/jpeg', 17290, '2009-03-12 12:06:03', 'Aspen-Townhomes-9.jpg', 'image/jpeg', 17339, '2009-03-12 12:06:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (649, 'Westgate Lakes Resort and Spa', 61, 'Set on one of Florida''s most scenic lakes, Westgate Lakes features comfortable resort villas with such amenities as a full kitchen, screened porch or patio, separate living and dining areas, leather furniture, and master bedroom with a color television and whirlpool bath. Just minutes from Walt Disney World, Universal Orlando, and SeaWorld, Westgate Lakes'' guests enjoy tennis courts, swimming pools, hot tubs, basketball, volleyball, paddleboats, planned adult and children''s activities, and a full-service spa. The on-site Smokehouse Grill is one of Orlando''s best barbecue restaurants.', '2009-03-12 12:08:26', '2009-03-12 12:08:26', 'westgatelakes1.jpg', 'image/jpeg', 3417, '2009-03-12 12:08:26', 'westgatelakes2.jpg', 'image/jpeg', 3917, '2009-03-12 12:08:24', 'westgatelakes3.jpg', 'image/jpeg', 2518, '2009-03-12 12:08:25', 'westgatelakes4.jpg', 'image/jpeg', 3432, '2009-03-12 12:08:25', 'westgatelakes5.jpg', 'image/jpeg', 4848, '2009-03-12 12:08:25');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (650, 'Wapato Point', 43, 'Located on 116 acres in north-central Washington, Wapato Point has amenities that include six tennis courts, one indoor and seven outdoor pools, and an indoor hot tub. You can rent a bicycle and explore. The resort is on Lake Chelan, which offers most water sports. Paddleboats, canoes, and sailboats are available for rent. In Chelan, seven miles away, you''ll find Slidewaters Amusement Park. In winter, cross-country and downhill skiing are available at nearby Echo Valley or, only an hour away, visit Mission Ridge, with a vertical drop of 2,000 feet. Seasonal recreational activities are available.', '2009-03-12 12:11:48', '2009-03-12 12:11:48', 'wapatopoint1.jpg', 'image/jpeg', 3328, '2009-03-12 12:11:47', 'wapatopoint2.jpg', 'image/jpeg', 4177, '2009-03-12 12:11:46', 'wapatopoint2.jpg', 'image/jpeg', 4177, '2009-03-12 12:11:46', 'wapatopoint3.jpg', 'image/jpeg', 3299, '2009-03-12 12:11:47', 'wapatopoint3.jpg', 'image/jpeg', 3299, '2009-03-12 12:11:47');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (652, 'Embassy Suites Poi Point', 28, 'Just 100 feet from the ocean, great ocean views, swimming pool, and a spectacular tropical garden. The beach is famous for surfing, snorkeling, and sunsets!', '2009-03-12 12:23:25', '2009-03-12 12:23:25', 'poipointe1.jpg', 'image/jpeg', 3966, '2009-03-12 12:23:25', 'poipointe2.jpg', 'image/jpeg', 3050, '2009-03-12 12:23:24', 'poipointe2.jpg', 'image/jpeg', 3050, '2009-03-12 12:23:24', 'poipointe3.jpg', 'image/jpeg', 6121, '2009-03-12 12:23:24', 'poipointe3.jpg', 'image/jpeg', 6121, '2009-03-12 12:23:25');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (653, ' Imperial Hawaii Vacation Club', 28, 'loha spirit is alive and well at the Imperial Hawaii Vacation Club, located 100 yards from Waikiki Beach--world famous for its swimming and surfing. Two blocks from the resort is the main shopping district of Waikiki--shops, restaurants, boutiques, and nightlife. The 27th-floor pool deck and 26th-floor owner''s lounge offer panoramic views of Waikiki, Honolulu, the mountains of Oahu, and the Pacific Ocean. The resort also features a full fitness center with a coed Jacuzzi and on-call masseuse. ', '2009-03-12 12:26:43', '2009-03-12 12:26:43', 'imperialhi1.jpg', 'image/jpeg', 3358, '2009-03-12 12:26:43', 'imperialhi2.jpg', 'image/jpeg', 3286, '2009-03-12 12:26:41', 'imperialhi3.jpg', 'image/jpeg', 4443, '2009-03-12 12:26:42', 'imperialhi4.jpg', 'image/jpeg', 3531, '2009-03-12 12:26:42', 'imperialhi5.jpg', 'image/jpeg', 2252, '2009-03-12 12:26:42');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (654, 'The Spa', 61, 'Troubling times call for tropical measures. Imagine turquoise waters and swaying palms. Cool trade winds and gracious hospitality. Indulge yourself at The Spa at Cheeca surrounded by cozy butler-serviced cabanas, shimmering pools, cascading waterfalls and lush tropical flora', '2009-03-12 12:29:58', '2009-03-12 12:29:58', 'spa1.jpg', 'image/jpeg', 4496, '2009-03-12 12:29:58', 'spa2.jpg', 'image/jpeg', 3936, '2009-03-12 12:29:56', 'spa2.jpg', 'image/jpeg', 3936, '2009-03-12 12:29:56', 'spa3.jpg', 'image/jpeg', 2968, '2009-03-12 12:29:57', 'spa3.jpg', 'image/jpeg', 2968, '2009-03-12 12:29:57');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (655, 'Lawai Beach Resort', 28, 'Lawai Beach Resort is located just outside the popular vacation center of Poipu Beach, a short walk from a fine snorkeling beach. In a peaceful, private setting, the resort has three outdoor pools with whirlpool and barbecue grills at each poolside, tennis courts, and a recreation center with fitness equipment. Concierges are ready to assist with island activities, to make the most of your Hawaiian vacation. Poipu Beach is known as one of the best surfing spots in Hawaii. Area waters also offer excellent opportunities for fishing, sailing, and windsurfing.', '2009-03-12 12:33:52', '2009-03-12 12:33:52', 'lawai1.jpg', 'image/jpeg', 3187, '2009-03-12 12:33:51', 'lawai2.jpg', 'image/jpeg', 3157, '2009-03-12 12:33:50', 'lawai3.jpg', 'image/jpeg', 3399, '2009-03-12 12:33:51', 'lawai4.jpg', 'image/jpeg', 3500, '2009-03-12 12:33:51', 'lawai5.jpg', 'image/jpeg', 3980, '2009-03-12 12:33:51');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (656, 'Tesoro Resort', 5, 'Experience the beauty of Mexico''s Pacific Coast. Tesoro''s Mexico resorts offer something for every traveler: white sand beaches, fishing and water sports, family activities, destination weddings, and premiere meeting facilities. Acquaint yourself with our properties at Los Cabos, Manzanillo, and Ixtapa. ', '2009-03-12 12:37:38', '2009-03-13 15:49:51', 'tesoro1.jpg', 'image/jpeg', 86483, '2009-03-13 15:49:50', 'tesoro2.jpg', 'image/jpeg', 101650, '2009-03-13 15:49:48', 'tesoro3.jpg', 'image/jpeg', 55052, '2009-03-13 15:49:49', 'tesoro4.jpg', 'image/jpeg', 52084, '2009-03-13 15:49:49', 'tesoro5.jpg', 'image/jpeg', 35140, '2009-03-13 15:49:50');
