INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (658, 'Marriott Palm Desert Villas', 20, 'Desert Vacation Villas is the perfect spot to relax and enjoy the beautiful desert scenery and excitement of Palm Springs. There are many superb golf courses in the area; understandably, Palm Springs is the site of more than 100 major golf tournaments annually. For on-site activities, there is an outdoor pool, whirlpool, bicycles, table tennis, and two barbecue grills. Play tennis on the courts at a park close by. Hiking, biking, and horseback riding are popular ways to explore the area''s desert landscape and canyons.', '2009-03-12 12:53:15', '2009-03-12 12:53:15', 'palmdessert1.jpg', 'image/jpeg', 3984, '2009-03-12 12:53:14', 'palmdessert2.jpg', 'image/jpeg', 4466, '2009-03-12 12:53:13', 'palmdessert3.jpg', 'image/jpeg', 3790, '2009-03-12 12:53:13', 'palmdessert4.jpg', 'image/jpeg', 3832, '2009-03-12 12:53:13', 'palmdessert5.jpg', 'image/jpeg', 3660, '2009-03-12 12:53:14');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (659, 'Fairmont Vacation Villas at Riverside', 52, 'Sister resort to Fairmont Vacation Villas at Mountainside and Hillside, Riverside is purpose-built to meet a variety of vacation needs. Located in the middle of a championship golf course and beside the Columbia River, the resorts offer a wide selection of activities year ''round and an on-site recreation center. A scenic panorama surrounds the resorts. Each building''s style and interior decor varies. This is our invitation to give you the opportunity to come enjoy Fabulous Fairmont Hot Springs Resort located at the headwaters of the Columbia River in the Windermere Valley. Enjoy our world famous mineral hot springs, Two 18-hole Championship Golf Courses, Ski Hill and much, much more! Come to the warm side of the Rockies, where getting away means having it all! ', '2009-03-12 13:00:56', '2009-03-12 13:00:56', 'fairmount2.jpg', 'image/jpeg', 3023, '2009-03-12 13:00:56', 'fairmount1.jpg', 'image/jpeg', 2491, '2009-03-12 13:00:54', 'fairmount4.jpg', 'image/jpeg', 2537, '2009-03-12 13:00:55', 'fairmount3.jpg', 'image/jpeg', 3279, '2009-03-12 13:00:55', 'fairmount5.jpg', 'image/jpeg', 2698, '2009-03-12 13:00:55');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (660, 'Sheraton Buganvilias Resort & Towers', 3, 'Situated in one of Mexico''s most popular vacation destinations, Buganvilias Resort Vacation Club is a sun-soaked delight. Try parasailing, surfing, deep-sea fishing or boating. The city retains the tropical trappings that made it famous in the film Night of the Iguana-the bougainvillea splashed across white-walled buildings, the swaying palm trees, and the cobblestoned streets.', '2009-03-12 13:05:57', '2009-03-12 13:05:57', 'shertonbugvallvista1.jpg', 'image/jpeg', 3778, '2009-03-12 13:05:57', 'shertonbugvallvista2.jpg', 'image/jpeg', 3465, '2009-03-12 13:05:55', 'shertonbugvallvista3.jpg', 'image/jpeg', 3854, '2009-03-12 13:05:56', 'shertonbugvallvista4.jpg', 'image/jpeg', 4699, '2009-03-12 13:05:56', 'shertonbugvallvista5.jpg', 'image/jpeg', 2861, '2009-03-12 13:05:56');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (661, 'Christmas Mountain Village', 33, 'Christmas Mountain Village invites you to enjoy year-round recreation amid the quiet beauty of the Wisconsin Dells. The Dells, fantastic rock formations that flank a 15-mile stretch of the Wisconsin River, are the state''s foremost natural attraction. The resort offers four outdoor pools open Memorial Day through Labor Day, one indoor pool, a 27-hole golf course, tennis courts, downhill ski slopes, a res- taurant, and lounge. Some activities such as swimming in the outdoor pool and skiing are seasonal. The Dells offers many summer -time activities', '2009-03-12 13:09:41', '2009-03-12 13:09:41', 'chirsmasmoutain1.jpg', 'image/jpeg', 3061, '2009-03-12 13:09:41', 'chirsmasmoutain2.jpg', 'image/jpeg', 3489, '2009-03-12 13:09:40', 'chirsmasmoutain3.jpg', 'image/jpeg', 3194, '2009-03-12 13:09:40', 'chirsmasmoutain4.jpg', 'image/jpeg', 2789, '2009-03-12 13:09:40', 'chirsmasmoutain5.jpg', 'image/jpeg', 4186, '2009-03-12 13:09:41');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (662, 'Fairfield Flagstaff', 56, 'Located on a 2,200-acre estate, Fairfield Flagstaff offers activities for all seasons. Only 80 miles from the Grand Canyon, the resort is a complete recreational community with an 18-hole golf course, eight tennis courts of which four are lighted, and two outdoor pools-- all open seasonally. An indoor pool (not affiliated with the resort) is available for a fee. Resort guests have access to the fitness center (no charge). Downhill skiing (subject to weather conditions) is a short drive away at the Arizona Snow Bowl. A million acres of national forest and historic sites abound.', '2009-03-12 13:31:34', '2009-03-12 13:31:34', 'flagstaff1.jpg', 'image/jpeg', 3555, '2009-03-12 13:31:33', 'flagstaff2.jpg', 'image/jpeg', 2889, '2009-03-12 13:31:31', 'fairways4.jpg', 'image/jpeg', 25486, '2009-03-12 13:31:32', 'fairways3.jpg', 'image/jpeg', 398141, '2009-03-12 13:31:32', 'flagstaff4.jpg', 'image/jpeg', 2744, '2009-03-12 13:31:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (663, 'Mizner Place', 61, 'The luxurious Mizner Place is located in the popular South Florida Bonaventure Spa, Tennis, and Golf community, home of the renowned Cliff Drysdale Tennis Center. Conveniently located within steps of your front door are restaurants, shopping, and nightlife located at the Town Center Shoppes. Tennis, golf, swimming, and a full schedule of activities are provided throughout the Vacation Village properties. Fort Lauderdale is only 25 miles away.', '2009-03-12 13:45:10', '2009-03-12 13:45:10', 'minzer1.jpg', 'image/jpeg', 2782, '2009-03-12 13:45:09', 'minzer2.jpg', 'image/jpeg', 4668, '2009-03-12 13:45:08', 'minzer3.jpg', 'image/jpeg', 3391, '2009-03-12 13:45:08', 'minzer4.jpg', 'image/jpeg', 3649, '2009-03-12 13:45:09', 'minzer5.jpg', 'image/jpeg', 3373, '2009-03-12 13:45:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (664, 'Club Solaris', 5, 'Royal Solaris Los Cabos offers a vacation full of activities and VIP services with a daily special fee for RCI members. Enjoy all meals and beverages in any of the four restaurants, snack bar, or three bars. A la carte dinners are in the Marco Polo or Panchos restaurants. A Gala dinner is offered once a week. The Tehuacan theater features a different show party daily. During the day, enjoy all four pools and social, sports, and recreational activities. Also included is a city tour. Your room''s coffee supply will be refilled daily.', '2009-03-12 13:51:22', '2009-03-12 13:51:22', 'clubsolaris1.jpg', 'image/jpeg', 2940, '2009-03-12 13:51:21', 'clubsolaris2.jpg', 'image/jpeg', 3593, '2009-03-12 13:51:20', 'clubsolaris3.jpg', 'image/jpeg', 4556, '2009-03-12 13:51:20', 'clubsolaris4.jpg', 'image/jpeg', 3616, '2009-03-12 13:51:21', 'clubsolaris5.jpg', 'image/jpeg', 2907, '2009-03-12 13:51:21');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (665, 'Club Sevilla', 61, 'Club Sevilla provides a convenient home base while you enjoy the famous attractions of Walt Disney World(R), Kennedy Space Center, Busch Gardens, and many other sights just a short drive from the resort. Bask amid the Mediterranean ambience as you take advantage of the resort''s amenities, including an outdoor heated swimming pool, whirlpool, tennis courts, and a clubhouse.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (666, 'Fairfield at The Medows', 62, 'Off the well-beaten path of country-singing celebrities and family entertainers lies a community south of the renowned Highway 76. The land claims host to Fairfield Branson. Close by, resort guests can enjoy relaxing days fishing at lakes, horseback riding, or playing tennis or golf. Evenings on the main strip of Branson provide foot-stomping fun as music theaters and attractions illuminate the streets with their neon glow.', '2009-03-12 14:01:30', '2009-03-12 14:01:30', 'fairfieldmedows1.jpg', 'image/jpeg', 3434, '2009-03-12 14:01:30', 'fairfieldmedows2.jpg', 'image/jpeg', 2112, '2009-03-12 14:01:29', 'fairfieldmedows2.jpg', 'image/jpeg', 2112, '2009-03-12 14:01:29', 'fairfieldmedows3.jpg', 'image/jpeg', 3139, '2009-03-12 14:01:29', 'fairfieldmedows3.jpg', 'image/jpeg', 3139, '2009-03-12 14:01:30');
