INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (668, 'Sea Garden Beach', 61, 'Sea Gardens Beach and Tennis Resort creates the perfect family seaside vacation. The resort is located just across the street from the beach, where you can bask in the sun or splash in ocean waves. Take a dip in the crystalline waters of the four on-site swimming pools (three are heated) or practice your serve on one of seven clay courts two blocks away or shuffleboard on two courts. You may also use the adjacent Fairfield Ocean Palms Resort.', '2009-03-13 12:30:54', '2009-03-13 12:30:54', 'seagardenfl1.jpg', 'image/jpeg', 4982, '2009-03-13 12:30:53', 'seagardenfl2.jpg', 'image/jpeg', 4395, '2009-03-13 12:30:52', 'seagardenfl3.jpg', 'image/jpeg', 4031, '2009-03-13 12:30:52', 'seagardenfl4.jpg', 'image/jpeg', 3998, '2009-03-13 12:30:53', 'seagardenfl5.jpg', 'image/jpeg', 3300, '2009-03-13 12:30:53');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (669, 'Lake Okanagan', 52, 'Lake Okanagan Resort is situated amidst 300 acres of natural parkland on the shores of Lake Okanagan. The resort features an executive par three golf courses, tennis courts, full service marina, beach, stables as well as miles of hiking and birding trails plus extensive summer activity programs. The area boasts over twenty wineries as well as numerous championship length golf courses, all within easy driving distance. Three downhill ski facilities are just over an hour''s drive away.', '2009-03-13 12:35:04', '2009-03-13 12:35:04', 'lakeokanagen1.jpg', 'image/jpeg', 2074, '2009-03-13 12:35:03', 'lakeokanagen2.jpg', 'image/jpeg', 3335, '2009-03-13 12:35:02', 'lakeokanagen3.jpg', 'image/jpeg', 2615, '2009-03-13 12:35:02', 'lakeokanagen4.jpg', 'image/jpeg', 3737, '2009-03-13 12:35:03', 'lakeokanagen5.jpg', 'image/jpeg', 3381, '2009-03-13 12:35:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (670, ' Royal Sea cliff Resort', 28, 'Royal Sea Cliff Resort is located on seven acres of beautiful prime oceanfront land just two miles south of Kailua Kona. The gracious Royal Sea Cliff Resort is a multi-level complex of striking architecture that descends toward the coast, affording dramatic views. On-site recreational amenities, such as the fresh or salt water pool, tennis court, and activity desk, combined with all the natural beauty of the island, will make this a truly memorable experience.', '2009-03-13 12:39:45', '2009-03-13 12:39:45', 'royalseacliff1.jpg', 'image/jpeg', 4340, '2009-03-13 12:39:45', 'royalseacliff2.jpg', 'image/jpeg', 4007, '2009-03-13 12:39:43', 'royalseacliff3.jpg', 'image/jpeg', 2815, '2009-03-13 12:39:44', 'royalseacliff4.jpg', 'image/jpeg', 2770, '2009-03-13 12:39:44', 'royalseacliff5.jpg', 'image/jpeg', 2632, '2009-03-13 12:39:44');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (671, 'Wyndham Worldmark', 39, 'WorldMark at Gleneden Beach is located on the coast of Oregon, known for its whale- watching. Close by is the Oregon Coast Aquarium located 30 minutes south in Newport, OR. There are other attractions in Newport as well. Golf is ten miles from the resort. The resort has an outdoor pool, tennis court, and hot tubs. A new gaming casino is located in nearby Lincoln City. The resort also has a large indoor recreation facility with video games, television, pool tables, a Ping- Pong table, and exercise equipment', '2009-03-13 12:51:56', '2009-03-13 12:51:56', 'wydhamworldmark1.jpg', 'image/jpeg', 3002, '2009-03-13 12:51:56', 'wydhamworldmark1.jpg', 'image/jpeg', 3002, '2009-03-13 12:51:54', 'wydhamworldmark1.jpg', 'image/jpeg', 3002, '2009-03-13 12:51:55', 'wydhamworldmark1.jpg', 'image/jpeg', 3002, '2009-03-13 12:51:55', 'wydhamworldmark1.jpg', 'image/jpeg', 3002, '2009-03-13 12:51:55');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (672, 'Los Cabos Golf Resort', 5, 'The Los Cabos Golf Resort is part of the Cabo San Lucas Country Club, a private 750-acre gate-guarded resort. These old Spanish colonial-style villas are set on the eighth and 16th tees. Guests will enjoy Dye championship golf on-site, as well as Nicklaus and Jones courses in the area. A nearby beach club is available for resort guests.', '2009-03-13 13:02:11', '2009-03-13 13:02:11', 'loscabogolf1.jpg', 'image/jpeg', 3465, '2009-03-13 13:02:11', 'loscabogolf2.jpg', 'image/jpeg', 3199, '2009-03-13 13:02:10', 'loscabogolf3.jpg', 'image/jpeg', 3315, '2009-03-13 13:02:10', 'loscabogolf4.jpg', 'image/jpeg', 3035, '2009-03-13 13:02:10', 'loscabogolf5.jpg', 'image/jpeg', 3794, '2009-03-13 13:02:11');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (673, 'Grand Timber Lodge', 55, 'Grand Timber Lodge is one of Colorado''s finest ski-in, ski-out resorts. Nestled between the Snowflake Chairlift and the BeaverRun SuperChair and just a short stroll from downtown Breckenridge, the resort features studio, one-, two-, and three-bedroom residences. You''ll find outdoor swimming pools, indoor hot tubs, an exercise room, and spa services. The one-, two-, and three-bedroom units have fully equipped kitchens, private decks, fireplaces, and satellite television with VCRs. Grand Timber also offers underground heated parking', '2009-03-16 12:22:37', '2009-03-16 12:22:37', 'grandtimberlodge1.jpg', 'image/jpeg', 4525, '2009-03-16 12:22:37', 'grandtimberlodge2.jpg', 'image/jpeg', 3509, '2009-03-16 12:22:35', 'grandtimberlodge3.jpg', 'image/jpeg', 3185, '2009-03-16 12:22:36', 'grandtimberlodge4.jpg', 'image/jpeg', 4104, '2009-03-16 12:22:36', 'grandtimberlodge5.jpg', 'image/jpeg', 3318, '2009-03-16 12:22:37');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (674, 'Lawrence Welk''s Desert Oasis', 20, 'At Lawrence Welk''s Desert Oasis, you''ll find the perfect vacation getaway. Adjacent to the resort are 27 holes of championship golf and 10 tennis courts. Take a refreshing dip in one of two swimming pools, work on a tan in the Southern California sun, or relax in the soothing waters of the spa. Your choices don''t stop there. You''ll find the heart of Palm Springs just minutes away, where you can shop with the stars on Palm Canyon Drive or dine at many popular restaurants', '2009-03-16 12:28:57', '2009-03-16 12:28:57', 'lawrencewelk1.jpg', 'image/jpeg', 3735, '2009-03-16 12:28:57', 'lawrencewelk2.jpg', 'image/jpeg', 3158, '2009-03-16 12:28:55', 'lawrencewelk3.jpg', 'image/jpeg', 3686, '2009-03-16 12:28:56', 'lawrencewelk4.jpg', 'image/jpeg', 2380, '2009-03-16 12:28:56', 'lawrencewelk5.jpg', 'image/jpeg', 3494, '2009-03-16 12:28:56');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (675, 'Bella Vista Village', 71, 'If you''ve a passion for golf, you''ll find no better place than the Greens at Bella Vista. There are 126 holes to conquer, all for a fee. The on-site pro shops offer rental clubs and a full line of golf apparel and equipment. You''ll also find tennis, badminton, volleyball, horseshoes, and an outdoor pool. The nearby village offers additional tennis and racquetball courts, four outdoor pools, an indoor pool, and a nature walking trail. Seven small lakes offer everything from skiing to fishing. See the countryside on horseback--stables offering guided trail rides are available.', '2009-03-16 12:46:36', '2009-03-16 12:46:36', 'bella1.jpg', 'image/jpeg', 3888, '2009-03-16 12:46:36', 'bella2.jpg', 'image/jpeg', 4598, '2009-03-16 12:46:35', 'bella3.jpg', 'image/jpeg', 4819, '2009-03-16 12:46:35', 'bella4.jpg', 'image/jpeg', 3700, '2009-03-16 12:46:35', 'bella5.jpg', 'image/jpeg', 3076, '2009-03-16 12:46:36');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (676, 'Kauai Beach Boy', 28, 'On the beautiful southern coast of Kauai, 51 lush tropical acres on Kalapaki Beach provide the garden setting  a community of vacation ownership suites,  a luxury hotel and conference center. Two restaurants and a world-class swimming pool complex are but a part of what comprises this spectacular resort. The quarter-mile beach frontage on Kalapaki Beach provides for all water and beach activities. Two championship golf courses are located at the adjacent Kauai Lagoons.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (677, 'San Jose del Cabo, the Barcelo''', 5, 'Located in the charming town of San Jose del Cabo, the Barcelo'' Los Cabos Resort & Spa is situated on a pristine golden beach overlooking the sparkling Sea of Cortez. You''ll be rejuvenated by innovative treatments available at the area''s only holistic spa, such as an ancient sweat purifying ritual called Temazcal. Or simply take refuge under a palapa on the beach with a cool drink in hand.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (678, 'Marriott''s Grande Vista', 61, 'Great golf is the hallmark attraction at Marriott''s Grande Vista at the Faldo Golf Institute by Marriott, but that''s only the beginning of what guests can expect. The distinctively designed one-, two-, and three-bedroom villas feature private balconies and panoramic views. For all the essentials, the Village Center features The MarketPlace convenience store. Just across the street is Marriott''s Grande Pines Golf Club, an 18-hole championship golf course scheduled to open in January 2004. And all the magic of Central Florida is waiting, an easy drive from these vacation villas. Construction is expected to continue through 2006.', '2009-03-17 12:35:50', '2009-03-17 12:35:50', 'marriotgrandvista1.jpg', 'image/jpeg', 3575, '2009-03-17 12:35:50', 'marriotgrandvista2.jpg', 'image/jpeg', 4467, '2009-03-17 12:35:48', 'marriotgrandvista3.jpg', 'image/jpeg', 3639, '2009-03-17 12:35:49', 'marriotgrandvista4.jpg', 'image/jpeg', 4525, '2009-03-17 12:35:49', 'marriotgrandvista5.jpg', 'image/jpeg', 3482, '2009-03-17 12:35:50');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (679, 'White Sands', 28, 'Waikiki Beach, home to sunny weather and great surfing, is just two blocks from the White Sands Waikiki Resort Club. Enjoy the entertainment, restaurants, and shops of Waikiki, and then relax by the on-site outdoor pool. The timeshare resort''s activities director can help arrange fishing, sailing, and snorkeling tours for you. Tennis and golf facilities are within two miles. The state capital, Honolulu, features Pearl Harbor''s USS Arizona Memorial, the International Market Place, Bishop Museum, and Honolulu Zoo.', '2009-03-17 12:53:01', '2009-03-17 12:53:01', 'whitesands1.jpg', 'image/jpeg', 3034, '2009-03-17 12:53:01', 'whitesands2.jpg', 'image/jpeg', 2785, '2009-03-17 12:53:00', 'whitesands2.jpg', 'image/jpeg', 2785, '2009-03-17 12:53:00', 'whitesands3.jpg', 'image/jpeg', 2710, '2009-03-17 12:53:00', 'whitesands3.jpg', 'image/jpeg', 2710, '2009-03-17 12:53:01');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (680, 'La Voliere du Mont-Tremblant', 60, 'Nestled near Mont-Tremblant Mountain, La Voliere du Mont-Tremblant provides a scenic spot for a vacation getaway. Wintertime action includes cross-country skiing, ice-skating, downhill skiing (with or without a ski school), sleigh rides, dog sled rides and snowmobiling. During the summer, enjoy the on-site pool or head to one of the area''s several lakes for fishing, swimming, and sailing. You can also tour the area at your own speed by mountain bike.La Voliere du Mont-Tremblant is located in Mont-Tremblant, Quebec in Canada and makes for a great vacation getaway. By renting a condo directly from an owner at La Voliere du Mont-Tremblant, which is a resort condominium, you will find your best vacation value! La Voliere du Mont-Tremblant offers a swimming pool and is near restaurants and shops in the Mont-Tremblant, Quebec area for your entertainment. Make La Voliere du Mont-Tremblant your Canada vacation getaway!', '2009-03-18 12:35:02', '2009-03-18 12:35:02', 'lavol1.jpg', 'image/jpeg', 19274, '2009-03-18 12:35:02', 'lavol2.jpg', 'image/jpeg', 13142, '2009-03-18 12:35:00', 'lavol3.jpg', 'image/jpeg', 13976, '2009-03-18 12:35:00', 'lavol4.jpg', 'image/jpeg', 13710, '2009-03-18 12:35:00', 'lavol5.jpg', 'image/jpeg', 13142, '2009-03-18 12:35:01');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (681, 'Sheraton Buganvilias Resort & Towers', 3, 'Situated in one of Mexico''s most popular vacation destinations, Buganvilias Resort Vacation Club is a sun-soaked delight. Try parasailing, surfing, deep-sea fishing or boating. The city retains the tropical trappings that made it famous in the film Night of the Iguana-the bougainvillea splashed across white-walled buildings, the swaying palm trees, and the cobblestoned streets. ', '2009-03-18 15:07:34', '2009-03-18 15:07:34', 'sher1.jpg', 'image/jpeg', 17866, '2009-03-18 15:07:34', 'sher2.jpg', 'image/jpeg', 14902, '2009-03-18 15:07:32', 'sher3.jpg', 'image/jpeg', 16902, '2009-03-18 15:07:32', 'sher4.jpg', 'image/jpeg', 20703, '2009-03-18 15:07:32', 'sher5.jpg', 'image/jpeg', 32124, '2009-03-18 15:07:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (682, 'Pahio at Bali Hai Villas', 28, 'PAHIO at Bali Hai Villas enjoys lovely mountain, garden, or ocean views from each unit and all the benefits of Kauai''s magnificent North Shore, including white- sand beaches, scenic waterfalls, cliffs, and activities to keep any vacationer happy. The beach is close by, or you can swim in the resort''s outdoor pools. Area activities are abundant and include horseback riding, fishing, surfing, snorkeling, and sailing. The resort''s activity directors are available to assist in planning your stay for maximum enjoyment. Golfers will find championship courses just blocks away.', '2009-03-18 20:35:42', '2009-03-18 20:35:42', 'pahiobali1.jpg', 'image/jpeg', 4881, '2009-03-18 20:35:42', 'pahiobali2.jpg', 'image/jpeg', 4157, '2009-03-18 20:35:40', 'pahiobali3.jpg', 'image/jpeg', 3907, '2009-03-18 20:35:41', 'pahiobali4.jpg', 'image/jpeg', 3053, '2009-03-18 20:35:41', 'pahiobali4.jpg', 'image/jpeg', 3053, '2009-03-18 20:35:42');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (683, 'The Sliver Lake', 20, 'Located in the California high desert, The Inn at Silver Lakes provides a perfect escape from the stress of city life. Clear skies and peace and quiet can be found, along with on-site activities, such as swimming in the pools, dining, dancing, and biking. The nearby Silver Lakes Clubhouse has a pool, a Jacuzzi, a 27-hole PGA-rated golf course (call for rules on attire and fees), exercise equipment, and a games room. The lake and local beach are one block away. Summer temperatures average between 95 and 110 degrees, and mild winters offer year-round golf and fishing. ', '2009-03-19 13:21:20', '2009-03-19 13:21:20', 'sliverlake1.jpg', 'image/jpeg', 86114, '2009-03-19 13:21:19', 'sliverlake2.jpg', 'image/jpeg', 10590, '2009-03-19 13:21:18', 'sliverlake3.jpg', 'image/jpeg', 31462, '2009-03-19 13:21:18', 'sliverlake4.jpg', 'image/jpeg', 11703, '2009-03-19 13:21:19', 'sliverlake4.jpg', 'image/jpeg', 11703, '2009-03-19 13:21:19');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (684, 'Pacific Monarch', 20, 'Pacific Monarch Resorts, Inc. is a well-established developer of five-star vacation ownership resorts specializing in resort locations and recreational amenities designed for active adults and families. PMR has consistently exceeded owner expectations and has earned a well-deserved reputation for delivering quality vacations. We are offering several exciting Palm Springs Getaway Packages to promote our new five-star Palm Canyon Resort. ', '2009-03-19 13:34:02', '2009-03-19 13:34:02', 'morcach1.jpg', 'image/jpeg', 18514, '2009-03-19 13:34:01', 'morcach2.jpg', 'image/jpeg', 2949, '2009-03-19 13:33:58', 'morcach2.jpg', 'image/jpeg', 2949, '2009-03-19 13:33:59', 'morcach3.jpg', 'image/jpeg', 3807, '2009-03-19 13:34:00', 'morcach3.jpg', 'image/jpeg', 3807, '2009-03-19 13:34:01');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (685, 'Island Links Resort, Hilton Head Island', 15, 'Tucked away among miles of sugar-sand beaches, the Island Links at Hilton Head offers guests a welcome respite. Enjoy a spacious condominium-villa complete with air-conditioning and private Jacuzzi hot tub. Priority tee times are available for guests at the many nearby golf courses, and guests are invited to use the adjacent fitness and spa facilities. Island Links offers a quiet balance of rejuvenation that will leave plenty of time to visit one of the resort''s three pools, explore miles of island-wide bike and nature trails connected to the resort, fishing, or gazing out at the resort''s beautiful lagoon.', '2009-03-19 13:36:20', '2009-03-19 13:36:20', 'island1.jpg', 'image/jpeg', 20766, '2009-03-19 13:36:20', 'island2.jpg', 'image/jpeg', 22834, '2009-03-19 13:36:18', 'island3.jpg', 'image/jpeg', 18631, '2009-03-19 13:36:18', 'island4.jpg', 'image/jpeg', 12697, '2009-03-19 13:36:19', 'island5.jpg', 'image/jpeg', 12948, '2009-03-19 13:36:19');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (686, 'Tahoe Beach & Ski Club', 20, 'Located on Lake Tahoe''s southern shore, Tahoe Beach & Ski Club boasts more than 400 feet of private beach. When you''re not on the lake, swim in the year-round heated outdoor pool or relax in the warmth of the sauna and hot tubs. Other summer activities include fishing, river rafting, golfing, horseback riding, and biking. In winter, Tahoe becomes a skier''s dream. There are more than 20 ski resorts within an easy drive. The slopes of Heavenly Valley are just a mile from the resort. Aside from downhill action, you''ll find cross-country skiing, sledding, and snowmobiling.', '2009-03-19 13:37:19', '2009-03-19 13:37:19', 'tahoe1.jpg', 'image/jpeg', 85361, '2009-03-19 13:37:19', 'tahoe2.jpg', 'image/jpeg', 40751, '2009-03-19 13:37:17', 'tahoe3.jpg', 'image/jpeg', 85431, '2009-03-19 13:37:18', 'tahoe4.jpg', 'image/jpeg', 70844, '2009-03-19 13:37:18', 'tahoe5.jpg', 'image/jpeg', 70199, '2009-03-19 13:37:19');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (687, 'Peppertree at Tamarack, WI', 33, 'Located midway between Chicago and Minneapolis, Wisconsin Dells is an ideal family vacation spot with many different and entertaining activities offered year- round. In the late fall and winter, you can ski, snowmobile and ice-skate in the area. In the spring and summer, take the kids to ride the "Ducks," on a scenic tour or to one of the thrilling water parks. The Dells offers such a variety of things to see and do, you can''t possibly experience it all in one vacation.', '2009-03-19 13:53:34', '2009-03-19 13:53:34', 'peppertree1.jpg', 'image/jpeg', 3892, '2009-03-19 13:53:34', 'peppertree2.jpg', 'image/jpeg', 2490, '2009-03-19 13:53:32', 'peppertree3.jpg', 'image/jpeg', 2902, '2009-03-19 13:53:33', 'peppertree4.jpg', 'image/jpeg', 2417, '2009-03-19 13:53:33', 'peppertree5.jpg', 'image/jpeg', 3228, '2009-03-19 13:53:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (688, 'Celebration World Resort, Orlando', 61, 'Celebration World Resort is a 91-acre, 840-unit vacation ownership resort being developed close to the town of Celebration in Osceola County (about five miles away). There is construction going on at the property. All construction is fenced in and should not disrupt your stay. Since the property is fairly new, there are limited amenities and activities at this time. The pool area and clubhouse are completed.', '2009-03-19 14:13:19', '2009-03-19 14:13:19', 'celebrationworld1.jpg', 'image/jpeg', 4019, '2009-03-19 14:13:18', 'celebrationworld2.jpg', 'image/jpeg', 2485, '2009-03-19 14:13:17', 'celebrationworld3.jpg', 'image/jpeg', 3733, '2009-03-19 14:13:17', 'celebrationworld4.jpg', 'image/jpeg', 2412, '2009-03-19 14:13:17', 'celebrationworld5.jpg', 'image/jpeg', 3145, '2009-03-19 14:13:18');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (689, 'Vacation Inn Villas, Collingwood', 49, 'From sailing on Georgian Bay to skiing on Blue Mountain, you''ll findadventure throughout the year at Vacation Inn Villas. Georgian Bay, borders the resort. At the resort, enjoy the two tennis courts or relax by the outdoor pool. Take a short drive to top-notch golf courses and horseback riding. Blue Mountain ski area, Ontario''s largest, is 3 miles from the resort. Year-round, you''ll appreciate the resort''s indoor pool, sauna, and whirlpool. The on-site restaurant is open for lunch and dinner. The City of Toronto is about 90 minutes drive away and Niagara Falls is about 3 hours drive away.', '2009-03-19 14:21:45', '2009-03-19 14:21:45', 'vacationwillascandana1.jpg', 'image/jpeg', 3543, '2009-03-19 14:21:45', 'vacationwillascandana2.jpg', 'image/jpeg', 3745, '2009-03-19 14:21:43', 'vacationwillascandana2.jpg', 'image/jpeg', 3745, '2009-03-19 14:21:44', 'vacationwillascandana3.jpg', 'image/jpeg', 2429, '2009-03-19 14:21:44', 'vacationwillascandana3.jpg', 'image/jpeg', 2429, '2009-03-19 14:21:44');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (690, 'Carlsbad Seapointe Resort', 20, 'Carlsbad Seapointe Resort overlooks the Pacific Ocean and is only 500 yards from one of the few undeveloped stretches of beach in San Diego''s North County. The quaint village of Carlsbad is a short drive to the north. San Diego is south, a short drive away. SeaWorld, San Diego Zoo, San Diego Wild Animal Park, and the Cabrillo National Park and Lighthouse are within 30 miles. The resort has many amenities to offer, including an outdoor adult pool and family pool, health club, and much more. In the evening, relax by the pool, lounge, or garden area and watch the sun slip into the Pacific Ocean.', '2009-03-19 16:06:17', '2009-03-19 16:06:17', 'carlsbadseapoint1.jpg', 'image/jpeg', 5433, '2009-03-19 16:06:17', 'carlsbadseapoint2.jpg', 'image/jpeg', 3803, '2009-03-19 16:06:15', 'carlsbadseapoint3.jpg', 'image/jpeg', 3337, '2009-03-19 16:06:15', 'carlsbadseapoint4.jpg', 'image/jpeg', 3569, '2009-03-19 16:06:16', 'carlsbadseapoint5.jpg', 'image/jpeg', 2863, '2009-03-19 16:06:16');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (691, 'Kala Point and Resorts West Vacation Club', 43, 'Kala Point and Resorts West Vacation Club at Kala Point is a private waterfront community with more than a mile of sandy beach on Port Townsend Bay, northwest of Seattle on the Olympic Peninsula. Activities include crabbing, clam-digging, fishing, and swimming at the beach or indoor/outdoor pool. The resort also offers a clubhouse and tennis courts. Every apartment has a private deck for enjoying the mountain view.', '2009-03-19 16:12:08', '2009-03-19 16:12:08', 'resortswest1.jpg', 'image/jpeg', 12150, '2009-03-19 16:12:07', 'resortswest2.jpg', 'image/jpeg', 10733, '2009-03-19 16:12:06', 'resortswest2.jpg', 'image/jpeg', 10733, '2009-03-19 16:12:06', 'resortswest3.jpg', 'image/jpeg', 9654, '2009-03-19 16:12:07', 'resortswest3.jpg', 'image/jpeg', 9654, '2009-03-19 16:12:07');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (701, 'Catalina Beach Club', 61, 'At Catalina Beach Club, you can swim in the heated outdoor pool, work out in the gym, or enjoy the waters of the Atlantic Ocean in your oceanfront unit. A children''s pool is also on site. Within a short drive, you will find facilities for biking, horseback riding, hiking, jogging, golfing, scuba diving, and playing tennis. Less than a mile away are many fine restaurants and nightclubs. Be sure to save a day or two to visit the Walt Disney World(R) theme parks and the Kennedy Space Center, each about an hour away.', '2009-03-20 16:38:31', '2009-03-20 16:38:31', 'catlina1.jpg', 'image/jpeg', 4213, '2009-03-20 16:38:31', 'catlina2.jpg', 'image/jpeg', 2737, '2009-03-20 16:38:29', 'catlina3.jpg', 'image/jpeg', 3755, '2009-03-20 16:38:30', 'catlina4.jpg', 'image/jpeg', 2877, '2009-03-20 16:38:30', 'catlina5.jpg', 'image/jpeg', 3877, '2009-03-20 16:38:30');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (692, 'The Manhattan Club', 69, 'With one of the best locations in New York City, The Manhattan Club, located at 56th Street and Seventh Avenue, offers access to some of the city''s most famous attractions. Carnegie Hall is located diagonally across the street from the resort, and Broadway''s famous theater district begins just around the corner. The world-famous shops of Fifth Avenue are only two blocks away, and Central Park is only a three-block stroll. You''ll be surrounded by the Big Apple''s best restaurants and close to Lincoln Center, the Museum of Modern Art, and all that makes New York a dynamic city.', '2009-03-19 16:19:25', '2009-03-19 16:19:25', 'manhattanclub1.jpg', 'image/jpeg', 3395, '2009-03-19 16:19:25', 'manhattanclub2.jpg', 'image/jpeg', 3547, '2009-03-19 16:19:24', 'manhattanclub3.jpg', 'image/jpeg', 3335, '2009-03-19 16:19:24', 'manhattanclub4.jpg', 'image/jpeg', 2815, '2009-03-19 16:19:24', 'manhattanclub5.jpg', 'image/jpeg', 3344, '2009-03-19 16:19:25');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (693, 'Costa de Oro Beach Club', 32, 'Welcome to the warm and sunny Mazatlan, most people come to this special place located in the Mexican Riviera to enjoy the fantastic golden beaches and the perfect weather. Costa de Oro is one of Mazatlan''s original beach front hotels. In Mazatlan, our beautiful facilities will go beyond the expected. Whether to relax or wanting to have some wild fun, Costa de Oro becomes the perfect setting for your dream vacation, there is just about every ingredient to satisfy even the most demanding client. For sure our friendly staff will make your vacation a truly enjoyable experience. The traditional hospitality and quality service of Costa de Oro is our trade mark. Swim in the resort''s pool, or challenge a friend to a tennis match. Follow fun-filled days with marvelous nights of dining and dancing and do it all right at the resort. We are conveniently located in the heart of the Golden Zone, where the most prestigious shops, restaurant and night life centers are at your finger tips. Stay with us and enjoy a vacation moment to last a lifetime.', '2009-03-19 16:29:52', '2009-03-19 16:29:52', 'costade1.JPG', 'image/jpeg', 90357, '2009-03-19 16:29:52', 'costade2.JPG', 'image/jpeg', 59271, '2009-03-19 16:29:50', 'costade3.JPG', 'image/jpeg', 37785, '2009-03-19 16:29:51', 'costade4.JPG', 'image/jpeg', 22349, '2009-03-19 16:29:51', 'costade5.JPG', 'image/jpeg', 37462, '2009-03-19 16:29:51');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (694, 'Sunset Lagoon Cancun', 4, 'Sunset Lagoon, a very charming mediterranean style hotel. Located on the lagoon side where you will get the most spectaclar sunsets in Cancun. A beautiful, elegant, and comfortbale way to spend your vacation is awaiting you at the Sunset Lagoon. -106 Standard Rooms & Suites -1 New fresh water swimming pool -Playground for children -Kids activities program -Scuba demo at the pool -3 Restaurants -1 New Snack Bar -3 Bars (with domestic brands, one of them being a swim up bar) -Day & Night time activities, please check schedule upon arrival -Non-motorized water sports such as, kayak, sailing boats, paddleboats, windsurfers. -New open-air theatre for night entertainment and shows -Safe deposit boxes available at the front desk -Bicycles at no charge -Satellite T.V. -Rent a car agent on premises. -Travel Agency -Shuttle bus to our sister hotel Royal Sunset & Maya Fair shopping mall. -Special guest discount at Maya Fair Mall -Video Bar with nightly entertainment -Theme parties -Night Disco tours -Shows and nightly entertainment
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (695, 'The Villas At Tree Tops', 65, 'An escape to the mountains awaits owners who select Tree Tops as their vacation getaway. Surrounded by spectacular views of a golf course and nestled in the Pocono Mountains, these villas offer guests a long list of on-site and nearby facilities and amenities. Its close proximity to Philiadelphia, New York, and a selection of nearby attractions, including the Deleware Water Gap National Recreation Area, the Crossings outlet shopping, NASCAR racing, museums, animal farms, snow tubing, and skiing, make this a year-round vacation destination.', '2009-03-19 16:57:23', '2009-03-19 16:57:23', 'treetops1.jpg', 'image/jpeg', 2954, '2009-03-19 16:57:22', 'treetops2.jpg', 'image/jpeg', 3245, '2009-03-19 16:57:21', 'treetops3.jpg', 'image/jpeg', 3183, '2009-03-19 16:57:21', 'treetops4444.jpg', 'image/jpeg', 3178, '2009-03-19 16:57:22', 'treetops5.jpg', 'image/jpeg', 2927, '2009-03-19 16:57:22');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (696, 'Massanutten Resort', 24, 'Experience a tradition of excellence at Massanutten''s Shenandoah Villas. With Virginia''s only quad chairlift and a vertical drop from 795 to 1,110 feet, the resort offers a thrilling challenge for downhill skiers and snowboarders. Also on site, enjoy two extensive sports complexes with an indoor pool, gymnasium, racquet- ball courts, exercise equipment, steam room, sauna, massage services, and child- ren''s programs. In summer, enjoy the out- door pools and tennis courts and mountain biking. A golf pro shop offers an 18-hole, PGA-rated or nine-hole executive course, putting green, and driving range', '2009-03-20 13:58:03', '2009-03-20 13:58:03', 'mass11111.jpg', 'image/jpeg', 3555, '2009-03-20 13:58:02', 'mass1.jpg', 'image/jpeg', 3507, '2009-03-20 13:58:00', 'mass2.jpg', 'image/jpeg', 3565, '2009-03-20 13:58:00', 'mass3.jpg', 'image/jpeg', 3336, '2009-03-20 13:58:01', 'mass4.jpg', 'image/jpeg', 3028, '2009-03-20 13:58:02');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (697, 'Wyndham Patriots Place', 24, 'Featuring on-and two- story condominium residences. Wyndham Patriot''s Place is just one mile from Historic Colonial Williamsburg. Patriots Place 2Our out-door recreational area and duck pond are ideal for family activities of all kinds. Rates subject to change during special events', '2009-03-20 14:02:43', '2009-03-20 14:02:43', 'patsplace1.jpg', 'image/jpeg', 4810, '2009-03-20 14:02:42', 'patsplace2.jpg', 'image/jpeg', 3663, '2009-03-20 14:02:41', 'patsplace3.jpg', 'image/jpeg', 3867, '2009-03-20 14:02:41', 'patsplace4.jpg', 'image/jpeg', 3784, '2009-03-20 14:02:41', 'patsplace5.jpg', 'image/jpeg', 2427, '2009-03-20 14:02:42');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (698, 'Havasu Dunes Resort', 56, 'Vacation at Havasu Dunes Resort, a mile from the center of Lake Havasu City and the London Bridge. Spend your days enjoying water sports on Lake Havasu, playing golf and tennis close by, or sunning by the three on-site swimming pools or whirlpool and hot tubs. The Laughlin, Nevada, gambling center is 70 miles away. Those looking for more scenic sights can take a trip to the Grand Canyon (230 miles away), Hoover Dam (135 miles away), Parker Dam (30 miles away), Oatman Ghost Town (70 miles away), or Flagstaff (200 miles away).', '2009-03-20 14:12:10', '2009-03-20 14:12:10', 'havasu1.jpg', 'image/jpeg', 3822, '2009-03-20 14:12:09', 'havasu2.jpg', 'image/jpeg', 3234, '2009-03-20 14:12:08', 'havasu3.jpg', 'image/jpeg', 2927, '2009-03-20 14:12:08', 'havasu4.jpg', 'image/jpeg', 4103, '2009-03-20 14:12:09', 'havasu5.jpg', 'image/jpeg', 3685, '2009-03-20 14:12:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (699, ' Resort Villas Vacation Club', 56, 'Allow your imagination and fantasy to guide you on this year''s vacation. Use this membership to travel where and when you want for that perfect vacation of a lifetime. Want romance, adventure, an exotic paradise, or easy access to great family attractions? Everything is within reach. Just call and allow our staff to help you realize your dream.', '2009-03-20 15:49:13', '2009-03-20 15:49:13', 'rvv1.jpg', 'image/jpeg', 41764, '2009-03-20 15:49:13', 'rvv2.jpg', 'image/jpeg', 76475, '2009-03-20 15:49:11', 'rvv3.jpg', 'image/jpeg', 15685, '2009-03-20 15:49:11', 'rvv2.jpg', 'image/jpeg', 76475, '2009-03-20 15:49:12', 'rvv1.jpg', 'image/jpeg', 41764, '2009-03-20 15:49:12');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (700, 'Royal Holiday Club', 36, 'The Royal Holiday vacation club affords you the opportunity to travel the United States and Mexico without joining an exchange company. Royal Holiday Club has occupancy rights at Sheraton Cancun Resort and Towers in Cancun; Sol Caribe in Cozumel; Sheraton Ixtapa, Mexico; the Raddison Xanadu in Freeport, Bahamas; Wave Crest in Del Mar, California; Powell Place, San Francisco; Marriott\'s Streamside at Vail; Tropicana Golf Estates in Las Vegas; High Point in Orlando; and Brigantine Beach, NJ.', '2009-03-20 16:16:53', '2009-03-20 16:16:53', 'rhc1.jpg', 'image/jpeg', 19253, '2009-03-20 16:16:53', 'rhc2.jpg', 'image/jpeg', 16411, '2009-03-20 16:16:51', 'rhc3.jpg', 'image/jpeg', 16441, '2009-03-20 16:16:52', 'rhc4.jpg', 'image/jpeg', 9440, '2009-03-20 16:16:52', 'rhc5.jpg', 'image/jpeg', 22459, '2009-03-20 16:16:52');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (702, 'Club Vacances Toutes Saisons', 60, 'Club Vacances Toutes Saisons is located near Mont Ste-Anne, Quebec''s premier ski area. Area attractions include touring beautiful Quebec City, the majestic St. Lawrence River, and the rustic scenery of Qu)bec. In the summer, the mountains become a hiker''s playground with beautiful pine woodlands and endless vistas. The resort has a tennis court and two pools on site (both seasonal). Please note: resort reflects French Canadian culture. Units are small but well appointed. There are limited amenities on site, but many activities are available in the area. No pets are allowed.', '2009-03-23 10:55:58', '2009-03-23 10:55:58', 'clubvacacenes1.jpg', 'image/jpeg', 2999, '2009-03-23 10:55:58', 'clubvacacenes2.jpg', 'image/jpeg', 2963, '2009-03-23 10:55:56', 'clubvacacenes3.jpg', 'image/jpeg', 2336, '2009-03-23 10:55:57', 'clubvacacenes4.jpg', 'image/jpeg', 3823, '2009-03-23 10:55:57', 'clubvacacenes5.jpg', 'image/jpeg', 2799, '2009-03-23 10:55:57');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (703, 'Liki Tiki Village', 61, 'Just minutes from the Walt Disney World Resort, Liki Tiki Village boasts spacious two-bedroom townhomes and one-, two-, and three-bedroom high-rise condominium accommodations. On-site, guests will enjoy two pools (heated during fall and winter), two whirlpools, a children''s pool, paddle boats, two lighted tennis courts, a game room, a sand volleyball court, picnic areas, and a freshwater lake. This resort features Liki Tiki Lagoon Water Adventure, with five waterslides, a wave pool, erupting volcano, and toddlers'' area, plus Shipwreck Sally''s Bar and Grill. Escape the ordinary at Island One Resorts'' Liki Tiki Village! Surrounded by swaying palms and lush tropical landscaping, this Interval International Five Star Resort offers you a host of resort-based activities that are sure bets for family fun. This 64-acre gated resort, which is nestled beside a sparkling lagoon, is designed for your perfect vacation. Liki Tiki Village has accommodations to suit families of all sizes. The two-bedroom/two-bath townhouse villas offer a tropical and airy atmosphere. The Reserve and Bali III feature luxurious and spacious one-, two- and three-bedroom villas. At Liki Tiki Village, you are centrally located in the heart of Orlando excitement -- only seven miles to the Walt Disney World® Resort entrance and just minutes from attractions including SeaWorld, Universal Studios and Pleasure Island. And be sure to experience the Liki Tiki Lagoon Water Adventure. This 220,000 gallon water park features five water slides, a wave pool, an erupting volcano and a toddler''s area. It''s guaranteed to exhilarate your whole family, and it''s right on-site for you! ', '2009-03-23 11:57:22', '2009-03-23 11:57:22', 'littlietikifl1.JPG', 'image/jpeg', 67451, '2009-03-23 11:57:22', 'littlietikifl2.JPG', 'image/jpeg', 42925, '2009-03-23 11:57:20', 'littlietikifl3.JPG', 'image/jpeg', 41543, '2009-03-23 11:57:20', 'littlietikifl4.JPG', 'image/jpeg', 43532, '2009-03-23 11:57:21', 'littlietikifl5.JPG', 'image/jpeg', 51907, '2009-03-23 11:57:21');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (704, 'Cliff Lodge', 46, 'Utah is known for light, fluffy powder. Even here, Snowbird stands out, with an average of 500 inches of annual snowfall. Snowbird''s flagship resort, The Cliff, is a steel-and-concrete bunker of a resort, built to withstand avalanches. Still, it seems every space has a huge window with a snow-draped view. Everything you need - when you''re not actually skiing - is within one building. So you can ditch the snow pants, heavy jacket and gloves and head to the restaurants, shopping and the spa.', '2009-03-23 12:02:16', '2009-03-23 12:02:16', 'CLIFFLODGE1.jpg', 'image/jpeg', 3262, '2009-03-23 12:02:16', 'CLIFFLODGE2.jpg', 'image/jpeg', 4118, '2009-03-23 12:02:14', 'CLIFFLODGE3.jpg', 'image/jpeg', 3041, '2009-03-23 12:02:14', 'CLIFFLODGE4.jpg', 'image/jpeg', 4175, '2009-03-23 12:02:15', 'CLIFFLODGE5.jpg', 'image/jpeg', 4204, '2009-03-23 12:02:15');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (705, 'Villa Del Arco', 5, 'Villa del Arco Beach Resort in Cabo San Lucas, Mexico is on world-famous El Medano Beach. The 5-Star up-scale beach resort features spacious units with beautiful ocean views and looks out on the famous Land''s End "El Arco", after which the resort is named
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (706, 'Church Street Inn', 15, 'Church Street Inn is located in the heart of historic Charleston, across the street from the old City Market, which is full of specialty shops, award-winning restaurants, and an open-air market for bargain hunters. By walking out the door, you can take a carriage ride around the historic downtown area or visit all of the city''s fine attractions. Discounted country club packages are available at nearby clubs and include golf, tennis, and swimming', '2009-03-23 12:19:01', '2009-03-23 12:19:01', 'CHURCHSTREET1.jpg', 'image/jpeg', 3486, '2009-03-23 12:19:00', 'CHURCHSTREET2.jpg', 'image/jpeg', 4596, '2009-03-23 12:18:58', 'CHURCHSTREET3.jpg', 'image/jpeg', 3731, '2009-03-23 12:18:59', 'CHURCHSTREET4.jpg', 'image/jpeg', 3652, '2009-03-23 12:18:59', 'CHURCHSTREET5.jpg', 'image/jpeg', 3515, '2009-03-23 12:19:00');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (707, 'Panama City Resort & Club', 61, 'Located on Florida''s Panhandle, Panama City Resort & Club offers vacationers a vast array of recreational activities. Guests can participate in all types of water activities, ranging from scuba diving to deep-sea fishing. Tennis is available 1.5 miles away, and golf is only four miles away, where resort guests receive discount greens fees. Area attractions include St. Andrews State Recreation Area, Miracle Strip Park, Ocean Opry, Zoo World, Panama City Pier, and Gulf World.', '2009-03-23 12:31:23', '2009-03-23 12:31:23', 'panamacity1.jpg', 'image/jpeg', 4368, '2009-03-23 12:31:22', 'panamacity2.jpg', 'image/jpeg', 3143, '2009-03-23 12:31:21', 'panamacity3.jpg', 'image/jpeg', 2525, '2009-03-23 12:31:21', 'panamacity4.jpg', 'image/jpeg', 2874, '2009-03-23 12:31:21', 'panamacity5.jpg', 'image/jpeg', 2819, '2009-03-23 12:31:22');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (708, 'Pueblo Bonito Rose Spa & Resort', 5, 'Pueblo Bonito Rose is located in the heart of Cabo San Lucas on El Medano beach. Los Cabos is famous for sportfishing, beaches, and extraordinary golf courses. The town of Cabo San Lucas is just minutes away, with an abundance of boutiques and shops offering custom-made beach and sportswear, designer fashions, and Mexican handicrafts and art. Pueblo Bonito Rose has recently opened a wonderful health and spa facilities room, offering four types of massages, a whirlpool, and a high tech exercise room, one of the best in the world.', '2009-03-23 12:45:22', '2009-03-23 12:45:22', 'pbrose1.jpg', 'image/jpeg', 3958, '2009-03-23 12:45:22', 'pb3.jpg', 'image/jpeg', 2886, '2009-03-23 12:45:20', 'pbrose2.jpg', 'image/jpeg', 3374, '2009-03-23 12:45:21', 'pbrose4.jpg', 'image/jpeg', 4232, '2009-03-23 12:45:21', 'pbrose5.jpg', 'image/jpeg', 3826, '2009-03-23 12:45:22');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (709, 'Leahigh Resort Club', 61, 'Lehigh Resort Club offers a secluded getaway from the crowds. Many leisure activities are available at the resort. Some units overlook the golf course. On-site amenities include a heated outdoor pool, tennis, miniature golf, and picnic areas with grills. Trips that complement the full spectrum of recreational activities are also available. Visit Sanibel Island or Fort Myers Beach--all within an hour''s drive. The Orlando theme parks, Key West, and the Everglades are just a few hours away by car', '2009-03-23 12:49:42', '2009-03-23 12:49:42', 'lehigh1.jpg', 'image/jpeg', 3498, '2009-03-23 12:49:42', 'lehigh2.jpg', 'image/jpeg', 4498, '2009-03-23 12:49:40', 'lehigh3.jpg', 'image/jpeg', 2939, '2009-03-23 12:49:41', 'lehigh4.jpg', 'image/jpeg', 2476, '2009-03-23 12:49:41', 'lehigh4.jpg', 'image/jpeg', 2476, '2009-03-23 12:49:41');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (710, 'Coronado Beach Resort', 20, 'The resort is the perfect blend of urban and beach atmospheres. Guests have full use of the Loews Coronado Bay Resort, only 4.5 miles away, which offers three pools, numerous activities, and a 30 percent discount on food and beverages. Coronado is quaint and relaxing, yet the sounds of the city add to the urban setting. The small units retain a casual elegance. The beach is a short walk away. Public transportation provides easy access to all parts of Coronado. Tours to Ensenada, Mexico, and Hollywood are also available.', '2009-03-23 12:54:32', '2009-03-23 12:54:32', 'coronado1.jpg', 'image/jpeg', 3583, '2009-03-23 12:54:32', 'coronado2.jpg', 'image/jpeg', 3034, '2009-03-23 12:54:30', 'coronado3.jpg', 'image/jpeg', 2711, '2009-03-23 12:54:30', 'coronado4.jpg', 'image/jpeg', 2969, '2009-03-23 12:54:31', 'coronado5.jpg', 'image/jpeg', 2583, '2009-03-23 12:54:31');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (711, 'Aventura Spa Palace', 4, 'The resort offers you a world of tropical vacation pleasures. Bask on the white-sand beaches; swim and snorkel in aquamarine waters; or try your luck at deep-sea fishing. Back on land, enjoy the resort''s tennis courts, gym, pool and restaurants, or you can tour the ancient Mayan ruins. This is an adults-only resort. There is also a museum of Spanish shipwrecks. Located on the mainland, the resort is a 40-minute ferry ride from the island of Cozumel; a fee is charged for the ferry. Palace Resorts owns Club de Golf Playacar and offers special prices for RCI members.', '2009-03-23 13:09:41', '2009-03-23 13:09:41', 'aventura1.jpg', 'image/jpeg', 4356, '2009-03-23 13:09:40', 'aventura2.jpg', 'image/jpeg', 4866, '2009-03-23 13:09:39', 'aventura3.jpg', 'image/jpeg', 4062, '2009-03-23 13:09:39', 'aventura4.jpg', 'image/jpeg', 3391, '2009-03-23 13:09:40', 'aventura5.jpg', 'image/jpeg', 3954, '2009-03-23 13:09:40');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (712, 'Marcus Vacation Club at Grand Geneva', 33, 'Marcus Vacation Club at Grand Geneva sits amid a panorama of rolling hills, riding stables, and breathtaking golf courses. Grand Geneva is west of Milwaukee and 90 minutes from Chicago. Grand Geneva''s seasons are rich with recreation, sports, and entertainment. On-site amenities include pools (indoor and outdoor); a water park; two award-winning golf courses; tennis courts; skiing and snowboarding areas; riding stables; restaurants; and a spa offering weight training, aerobics, and lavish spa treatments. Most of these activities require an additional fee.', '2009-03-23 13:14:14', '2009-03-23 13:14:14', 'GRANDGEVEVA1.jpg', 'image/jpeg', 3287, '2009-03-23 13:14:13', 'GRANDGEVEVA2.jpg', 'image/jpeg', 3380, '2009-03-23 13:14:12', 'GRANDGEVEVA3.jpg', 'image/jpeg', 3185, '2009-03-23 13:14:12', 'GRANDGEVEVA4.jpg', 'image/jpeg', 3342, '2009-03-23 13:14:13', 'GRANDGEVEVA5.jpg', 'image/jpeg', 2762, '2009-03-23 13:14:13');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (713, 'The Georgian Manor Resort and Country Club', 49, 'Year-round activities--from swimming at the world''s longest freshwater beach at Wasaga on Georgian Bay to skiing at Ontario''s largest Alpine ski area, Blue Mountain--make the resort a perfect vacation destination. Nearby you''ll find fishing, golf courses, and riding stables. On-site options include an indoor and an outdoor pool, volleyball, a sauna, tennis, a whirlpool, an 18-hole pro putting golf course, a restaurant, and a lounge. Cross-country skiing and snowmobiling trails add to the winter fun. The city of Toronto is about 90 minutes away and Niagara Falls is about three hours away', '2009-03-23 13:31:46', '2009-03-23 13:31:46', 'georgian1.jpg', 'image/jpeg', 3759, '2009-03-23 13:31:46', 'georgian2.jpg', 'image/jpeg', 4074, '2009-03-23 13:31:44', 'georgian3.jpg', 'image/jpeg', 2529, '2009-03-23 13:31:44', 'georgian4.jpg', 'image/jpeg', 2429, '2009-03-23 13:31:45', 'georgian5.jpg', 'image/jpeg', 2738, '2009-03-23 13:31:45');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (714, 'Tropicana Carib', 41, 'Turquoise waters and white sands are in abundance in Puerto Plata. Tropicana Caribe is just steps away from the beach and all the water activities you can imagine. An 18-hole, Robert Trent Jones golf course is just a few minutes away, in addition to dozens of fine restaurants, discotheques, and opportunities for shopping. Or a lazy day by the resort''s outdoor pool may be all your heart desires. Whichever it is, action or relaxation, you''ll find it here', '2009-03-23 13:34:07', '2009-03-23 13:34:07', 'tropica1.jpg', 'image/jpeg', 3776, '2009-03-23 13:34:06', 'tropica1.jpg', 'image/jpeg', 3776, '2009-03-23 13:34:05', 'tropica1.jpg', 'image/jpeg', 3776, '2009-03-23 13:34:05', 'tropica2.jpg', 'image/jpeg', 2870, '2009-03-23 13:34:05', 'tropica2.jpg', 'image/jpeg', 2870, '2009-03-23 13:34:06');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (715, 'Golden Strand Ocean villa', 61, 'Golden Strand Ocean Villa Resort is located in Miami Beach, allowing you to enjoy both Miami and Fort Lauderdale. In addition to its private beach area, the resort offers an outdoor pool, European spa, coffee shop, and poolside bar. Water sports, golf, and tennis can be enjoyed close by for a nominal fee. Be sure to visit Bal Harbour, Coconut Grove, the Art Deco District, and the many area nightclubs and restaurants. The concierge can make arrangements for you to tour area attractions.', '2009-03-23 14:13:56', '2009-03-23 14:13:56', 'goldenstrand1.jpg', 'image/jpeg', 4051, '2009-03-23 14:13:56', 'goldenstrand2.jpg', 'image/jpeg', 3144, '2009-03-23 14:13:54', 'goldenstrand3.jpg', 'image/jpeg', 4136, '2009-03-23 14:13:55', 'goldenstrand4.jpg', 'image/jpeg', 2929, '2009-03-23 14:13:55', 'goldenstrand4.jpg', 'image/jpeg', 2929, '2009-03-23 14:13:55');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (716, 'Marriott Barony Beach Club', 15, 'Marriott''s Barony Beach Club is located on Port Royal Plantation, a few minutes'' drive from all that the island has to offer. The beach is across from the entrance of a neighboring resort, and is within easy walking distance from the villas. Each spacious, two-bedroom villa includes a private balcony, whirlpool, fully equipped kitchen, utility room with washer/dryer, and living and dining areas. Guests will enjoy six private pools, five whirlpool spas, putting green, shuffleboard courts, barbecue grills, a world-class spa, convenience store, and deli.', '2009-03-23 14:31:10', '2009-03-23 14:31:10', 'marriotbarony1.jpg', 'image/jpeg', 3618, '2009-03-23 14:31:10', 'marriotbarony2.jpg', 'image/jpeg', 2574, '2009-03-23 14:31:08', 'marriotbarony3.jpg', 'image/jpeg', 3367, '2009-03-23 14:31:09', 'marriotbarony4.jpg', 'image/jpeg', 2961, '2009-03-23 14:31:09', 'marriotbarony5.jpg', 'image/jpeg', 3993, '2009-03-23 14:31:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (717, 'Beach Palace, Cancun', 4, 'Beach Palace is an all-inclusive resort with standard rooms and suites. Located right on the beach, it is also within walking distance of a shopping center. There are three restaurants on site, along with two bars, a scuba tank, full activities program, special program for kids, and theme parties during the week. Palace Resorts owns Golf Club Playacar, designed by Robert Von Hagge. Information is available at the guest service desk. There are special prices for RCI members', '2009-03-23 14:36:28', '2009-03-23 14:36:28', 'beachclub1.jpg', 'image/jpeg', 5127, '2009-03-23 14:36:28', 'beachclub2.jpg', 'image/jpeg', 2744, '2009-03-23 14:36:27', 'beachclub3.jpg', 'image/jpeg', 3691, '2009-03-23 14:36:27', 'beachclub4.jpg', 'image/jpeg', 3813, '2009-03-23 14:36:27', 'beachclub5.jpg', 'image/jpeg', 2766, '2009-03-23 14:36:28');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (718, 'Marriott''s Summit Watch', 46, 'Located at the base of historic Main Street, Marriott''s Summit Watch is steps away from the town ski lift, in the heart of beautiful Park City. While the area boasts of having "the greatest snow on Earth" for skiers, it also offers outstanding year-round recreational opportunities including great golf, tennis, mountain biking, fishing, and hiking, as well as cultural events, shopping, and fine dining. The resort features elegantly appointed villas, whirlpool spas, swimming pool, and exercise facilities', '2009-03-23 15:04:41', '2009-03-23 15:04:41', 'summitwatch5.jpg', 'image/jpeg', 3395, '2009-03-23 15:04:41', 'summitwatch1.jpg', 'image/jpeg', 3644, '2009-03-23 15:04:39', 'summitwatch2.jpg', 'image/jpeg', 4763, '2009-03-23 15:04:40', 'summitwatch3.jpg', 'image/jpeg', 3433, '2009-03-23 15:04:40', 'summitwatch4.jpg', 'image/jpeg', 4488, '2009-03-23 15:04:40');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (727, 'Bonnet Creek Resort', 61, 'Escape the familiar and experience a truly wonderful vacation. Bonnet Creek provides a rainbow of exciting possibilities to set you free and make it easy to have fun. you\'ll find yourselves just minutes (count them) from the gates of the world\'s most magical theme parks. And your stylish resort home comes with a swimming pool with lazy river, wading pool with interactive water features and outdoor hot tub, all very relaxing and best of allit\'s yours to enjoy.', '2009-03-24 11:36:22', '2009-03-24 11:36:22', 'bonnettcreek1.jpg', 'image/jpeg', 5103, '2009-03-24 11:36:22', 'bonnettcreek2.jpg', 'image/jpeg', 4756, '2009-03-24 11:36:20', 'bonnettcreek3.jpg', 'image/jpeg', 4671, '2009-03-24 11:36:21', 'bonnettcreek4.jpg', 'image/jpeg', 4301, '2009-03-24 11:36:21', 'bonnettcreek5.jpg', 'image/jpeg', 3904, '2009-03-24 11:36:21');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (728, 'La Voliere Du Mont-Tremblant', 49, 'Nestled near Mont-Tremblant Mountain, La Voliere du Mont-Tremblant provides a scenic spot for a vacation getaway. Wintertime action includes cross-country skiing, ice-skating, downhill skiing (with or without a ski school), sleigh rides, dog sled rides and snowmobiling. During the summer, enjoy the pool on site or go to one of the several lakes in the area for fishing, swimming, and sailing. You can also tour the area at your own speed by mountain bike. You will find numerous attractions in the city of Montr)al which is 1.5 hours away.', '2009-03-24 11:49:12', '2009-03-24 11:49:12', 'lav1.jpg', 'image/jpeg', 3005, '2009-03-24 11:49:11', 'lav2.jpg', 'image/jpeg', 3413, '2009-03-24 11:49:10', 'la3.jpg', 'image/jpeg', 3267, '2009-03-24 11:49:10', 'lav4.jpg', 'image/jpeg', 2896, '2009-03-24 11:49:10', 'lav5.jpg', 'image/jpeg', 1888, '2009-03-24 11:49:11');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (719, 'King''s Creek Plantation, Williamsburg', 24, 'King''s Creek Plantation, a Gold Crown resort, member of II & RCI is located in Williamsburg, Virginia. In addition to the King''s Creek resorts pool, outdoor spa, and tennis courts, they are in the process of building a state of the art fitness & health center & indoor pool. The resort design, centers around a cottage style community, with free standing 3 bedroom cottages. The floor plan offering a collection of opportunities, 3 bedroom, 2 bedroom and 1 bedroom stays. Available vacation plans allow for multiple stays and trades that allow you to custom design an unique, yearly itinerary. A World-Class Resort off the "King''s Creek" of Williamsburg, Virginia and located in one of the nation''s finest vacation spots. The resort is accessible from the Colonial Parkway and from I-64 and Route 199, and is convenient to hundreds of leisure opportunities in town, nearby and within a day''s excursion distance. There are hundreds of leisure activities in town. Although town is best-known for Colonial Williamsburg, the historic restoration in its center, Williamsburg abounds in recreation opportunities: golf, shopping, fine food and entertainment. In addition to the King''s Creek resort''s heated pool, outdoor spa, and tennis courts, nearby Williamsburg has facilities for boating, canoeing, and miniature golf. Within a radius of a few miles are Water Country USA (right next door to the resort), Busch Gardens, Colonial Williamsburg, and historic Jamestown and Yorktown. Williamsburg also boasts more than 50 restaurants and a multitude of public golf courses, bike rental shops, health clubs, shopping centers and theaters. ', '2009-03-23 15:08:03', '2009-03-23 15:08:03', 'kingscreek1.jpg', 'image/jpeg', 3313, '2009-03-23 15:08:03', 'kingscreek2.jpg', 'image/jpeg', 3126, '2009-03-23 15:08:01', 'kingscreek3.jpg', 'image/jpeg', 3173, '2009-03-23 15:08:02', 'kingscreek4.jpg', 'image/jpeg', 3161, '2009-03-23 15:08:02', 'kingscreek5.jpg', 'image/jpeg', 2918, '2009-03-23 15:08:02');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (720, 'Fairfield Glade', 63, 'Fairfield Glade, set on 12,500 acres of the Cumberland Plateau, offers a vacation for the entire family. The resort is home to a 36-hole golf course and three 18-hole courses, including Stonehenge, listed among Golf Digest''s 75 best resort courses in America. An on-site recreation area with fees features miniature golf, indoor and outdoor tennis courts, and indoor and outdoor pools. Enjoy fishing/boating and swimming at any of the 12 no-wake lakes (no waterskiing is allowed). The resort has an on-site shopping mall, bank, hair salon, and grocery. A car is recommended to enjoy the area', '2009-03-23 15:12:10', '2009-03-23 15:12:10', 'fairfieldglade1.jpg', 'image/jpeg', 12391, '2009-03-23 15:12:09', 'fairfieldglade1.jpg', 'image/jpeg', 12391, '2009-03-23 15:12:08', 'fairfieldglade1.jpg', 'image/jpeg', 12391, '2009-03-23 15:12:08', 'fairfieldglade2.jpg', 'image/jpeg', 17511, '2009-03-23 15:12:09', 'fairfieldglade2.jpg', 'image/jpeg', 17511, '2009-03-23 15:12:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (721, 'Bentley Brook Mountain Club', 26, 'Bentley Brook, located in the Jiminy Peak ski area, offers a variety of on-site amenities--from downhill skiing to swimming and biking. The Berkshire area also provides a vast array of cultural and historic attractions. Visit Tanglewood, summer home for the Boston Symphony, The Hancock Shaker Village, Williamstown Theater, and Norman Rockwell Museum. Since the turn of the century, the Berkshires have been the place for music, theater, dance, and the arts. A car is recommended to enjoy the area. Contact the resort for information on amenities and fees
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (722, 'Marriott''s Kauai Beach Club', 28, 'On the beautiful southern coast of Kauai, 51 lush tropical acres on Kalapaki Beach provide the garden setting for Marriott''s Kauai Beach Club, a community of vacation ownership suites, and the Kauai Marriott Resort on Kalapaki Beach, a luxury hotel and conference center. Two restaurants and a world-class swimming pool complex are but a part of what comprises this spectacular resort. The quarter-mile beach frontage on Kalapaki Beach provides for all water and beach activities. Two championship golf courses are located at the adjacent Kauai Lagoons.', '2009-03-23 15:44:10', '2009-03-23 15:44:10', 'kauimarriot1.jpg', 'image/jpeg', 9506, '2009-03-23 15:44:10', 'kauimarriot2.jpg', 'image/jpeg', 21757, '2009-03-23 15:44:08', 'kauimarriot3.jpg', 'image/jpeg', 22240, '2009-03-23 15:44:08', 'kauimarriot4.jpg', 'image/jpeg', 23812, '2009-03-23 15:44:09', 'kauimarriot5.jpg', 'image/jpeg', 5514, '2009-03-23 15:44:09');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (723, 'Moon Palace Resort Cancun', 4, 'The Moon Palace is one of the Five Star Palace Resorts. An all-inclusive resort located on the tranquil shores of the Mayan Riviera, south of Cancun. At the Moon Palace, you will enjoy a truly secluded paradise. It is expansive yet intimate; isolated yet minutes from the nightlife of Cancun. If you want to soak up some sun sitting next to a pool, you will have a variety to choose from. There are indoor and outdoor pools and pools in the restaurants. The resort is nestled between a pristine white sand beach and the breathtaking beauty of 55 acres of tropical foliage and features 2,031 elegantly appointed Five Star guest rooms all with private balcony overlooking the spectacular Caribbean Sea', '2009-03-23 15:52:32', '2009-03-23 15:52:32', 'moon1.jpg', 'image/jpeg', 26196, '2009-03-23 15:52:31', 'moon2.jpg', 'image/jpeg', 28479, '2009-03-23 15:52:30', 'moon3.jpg', 'image/jpeg', 13585, '2009-03-23 15:52:30', 'moon4.jpg', 'image/jpeg', 21985, '2009-03-23 15:52:30', 'moon5.jpg', 'image/jpeg', 3673, '2009-03-23 15:52:31');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (724, 'Flagship Resort', 58, 'Located in the heart of Atlantic City''s historic inlet district, the Flagship''s 32-story tower stands as the ultimate fun-filled vacation destination. Spectacular views from the suites encompass the world-famous boardwalk, the casinos, the marina, and the ocean. In addition to the sun and surf, the Flagship features the Ozone Bar & Grille, an indoor pool and hot tub, a fitness center, game room, and a sundeck with two outdoor hot tubs. The resort also provides personalized concierge service along with a complimentary casino shuttle.', '2009-03-23 15:55:20', '2009-03-23 15:55:20', 'flagship1.jpg', 'image/jpeg', 27897, '2009-03-23 15:55:20', 'flagship2.jpg', 'image/jpeg', 23631, '2009-03-23 15:55:18', 'flagship2.jpg', 'image/jpeg', 23631, '2009-03-23 15:55:18', 'flagship3.jpg', 'image/jpeg', 18192, '2009-03-23 15:55:19', 'flagship3.jpg', 'image/jpeg', 18192, '2009-03-23 15:55:19');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (725, 'Sunrise Cove at Village West, IA', 50, 'Sunrise Cove at Village West is surrounded by a four-season vacation adventureland. In the summer, your day might include lounging on sandy beaches, shopping, antiques hunting, enjoying an amusement park, or splashing through Boji Bay. Boating, fishing, and other water sports abound on the blue waters of Lake Okoboji. Fall and spring activities in the area include golf, tennis, and hunting. In winter, relax in the indoor pool and Jacuzzi or brave the elements for ice- skating, cross-country skiing, and snowmobiling .', '2009-03-23 16:11:37', '2009-03-23 16:11:37', 'sunrisecoveiw3.jpg', 'image/jpeg', 37356, '2009-03-23 16:11:37', 'sunrisecoveiw3.jpg', 'image/jpeg', 37356, '2009-03-23 16:11:35', 'sunrisecoveiw3.jpg', 'image/jpeg', 37356, '2009-03-23 16:11:36', 'sunrisecoveiw5.jpg', 'image/jpeg', 25500, '2009-03-23 16:11:36', 'sunrisecoveiw5.jpg', 'image/jpeg', 25500, '2009-03-23 16:11:37');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (726, 'Pacific Shores Resort and Spa', 52, 'Located directly on Craig Bay, Pacific Shores Nature Resort is perfectly situated for sightseeing excursions on Vancouver Island. The lush, 15-acre, oceanfront property offers fantastic, themed floral gardens with more than 1,500 species and guided tours, a secluded beach, water activities, including salmon fishing and complimentary kayaks. Eight golf courses are within a 20-minute drive. Begin your day in the pool and fitness center, and unwind at night in the 24-hour hot tub located in the beachside cabana where you will also find a barbecue grill.', '2009-03-24 11:31:44', '2009-03-24 11:31:44', 'pacificshores1.jpg', 'image/jpeg', 13792, '2009-03-24 11:31:44', 'pacificshores2.jpg', 'image/jpeg', 3261, '2009-03-24 11:31:42', 'pacificshores3.jpg', 'image/jpeg', 3647, '2009-03-24 11:31:43', 'pacificshores4.jpg', 'image/jpeg', 4631, '2009-03-24 11:31:43', 'pacificshores5.jpg', 'image/jpeg', 3532, '2009-03-24 11:31:43');
