INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (730, 'Royal Sunset Cancun', 4, 'The Royal Sunset is an all-inclusive hotel located 12.5 miles from Cancun International Airport. An outstanding and inviting property, this hotel is the best place to call home while traveling on business or pleasure. The great number of amenities, facilities and extras this hotel features makes it an enjoyable place to stay. An overall atmosphere of tranquility will delight guests when they check in, for this hotel is well attuned to the individual needs of all its visitors. In addition, area attractions and venues that guests will want to experience are within the vicinity, making it fun to get to know the area. Come check it out for your own good. ', '2009-03-24 12:04:14', '2009-03-24 12:04:14', 'RSC1.jpg', 'image/jpeg', 4024, '2009-03-24 12:04:14', 'rsc2.jpg', 'image/jpeg', 2272, '2009-03-24 12:04:12', 'rsc3.jpg', 'image/jpeg', 3633, '2009-03-24 12:04:13', 'rsc4.jpg', 'image/jpeg', 2887, '2009-03-24 12:04:13', 'rsc5.jpg', 'image/jpeg', 2376, '2009-03-24 12:04:13');
