INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (732, 'Schooner Beach & Racquet Club', 15, 'Schooner Beach & Racquet Club is within easy reach of entertainment spots, amusement parks, shopping, and restaurants. Theaters include The Carolina Opry, Dixie Stampede, Ledends In Concert, and Alabama Theater. Myrtle Beach boasts more than 1,700 restaurants including Hard Rock Cafe, House of Blues, Planet Hollywood, NASCAR Cafe, and Bullwinkles. There are more than 100 championship golf courses. The resort is just a few steps from the beach, and all rooms have views of the oceanfront. Free tennis, an outdoor pool, exercise room, and sauna are available.', '2009-03-24 12:24:46', '2009-03-24 12:24:46', 'schooner1.jpg', 'image/jpeg', 3328, '2009-03-24 12:24:45', 'schooner2.jpg', 'image/jpeg', 3432, '2009-03-24 12:24:44', 'schooner3.jpg', 'image/jpeg', 3227, '2009-03-24 12:24:44', 'schooner4.jpg', 'image/jpeg', 2366, '2009-03-24 12:24:44', 'schooner5.jpg', 'image/jpeg', 3263, '2009-03-24 12:24:45');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (733, 'Banff Rocky Mountain', 42, 'Banff Rocky Mountain resort is located in a spectacular setting within Banff National Park. The resort features a fitness club with exercise equipment, squash, indoor pool, indoor/outdoor hot tubs and restaurants. Winter offers downhill skiing at Sunshine Village, Mount Norquay, and Lake Louise; cross-country skiing in Banff National Park; heli-skiing in the Bugaboos; and sleigh rides. Summer offers heli-hiking, area hot springs, fishing, golf, horseback riding and boating. To top it all a myraid of shops and restaurants await visitors in the town of Banff.', '2009-03-24 12:44:02', '2009-03-24 12:44:02', 'banff1.jpg', 'image/jpeg', 4206, '2009-03-24 12:44:02', 'banff2.jpg', 'image/jpeg', 3630, '2009-03-24 12:44:00', 'banff3.jpg', 'image/jpeg', 3516, '2009-03-24 12:44:00', 'banff4.jpg', 'image/jpeg', 4114, '2009-03-24 12:44:01', 'banff5.jpg', 'image/jpeg', 3058, '2009-03-24 12:44:01');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (734, 'Coral Reef Resort', 15, 'Coral Reef Resort and Spa is located in beautiful Shipyard Plantation. This plantation encompasses 883 acres on Hilton Head Island''s eastern shore. The plantation has a full array of amenities available, including golf, tennis, bicycle trails, and a beach one mile away. Hilton Head provides a wide assortment of dining and entertainment options to put a finishing touch on your vacation days.', '2009-03-24 12:51:18', '2009-03-24 12:51:18', 'COARLREEF1.jpg', 'image/jpeg', 2029, '2009-03-24 12:51:17', 'COARLREEF2.jpg', 'image/jpeg', 3071, '2009-03-24 12:51:16', 'COARLREEF2.jpg', 'image/jpeg', 3071, '2009-03-24 12:51:16', 'COARLREEF3.jpg', 'image/jpeg', 3127, '2009-03-24 12:51:17', 'COARLREEF3.jpg', 'image/jpeg', 3127, '2009-03-24 12:51:17');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (735, 'Rancho Banderas Vacation Villas', 3, 'Just 25 kilometers'' drive north of Puerto Vallarta, Rancho Banderas Vacation Villas is located on a stretch of white, sandy beach. The appeal of this self-contained, family-oriented resort is enhanced by the abundance of recreational activities available. Rancho Banderas combines the beauty of Mexican colonial architecture with comfortable accommodations.', '2009-03-24 12:56:07', '2009-03-24 12:56:07', 'rancho1.jpg', 'image/jpeg', 41659, '2009-03-24 12:56:06', 'rancho2.jpg', 'image/jpeg', 46371, '2009-03-24 12:56:05', 'rancho3.jpg', 'image/jpeg', 52177, '2009-03-24 12:56:05', 'rancho4.jpg', 'image/jpeg', 54257, '2009-03-24 12:56:06', 'rancho5.jpg', 'image/jpeg', 55381, '2009-03-24 12:56:06');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (736, 'Geo Holiday', 49, 'Many people who use GeoHoliday may not know what vacation ownership is or how large the membership has grown. Here are a few industry facts concerning vacation ownerships.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (737, 'Maui Banyan Vacation Club', 28, 'Maui Banyan Vacation Club puts you at home with the convenience of beautifully decorated two-bedroom villas, which include a separate lock-off with a kitchenette and cable television. The resort is located in sunny Kihei, cradled between the slopes of Haleakala and a shimmering ocean, and near the prestigious 1,500-acre master-planned resort area of Wailea, famed for its lavish hotels, serenity, and beauty. For the ultimate in vacationing, each two-bedroom villa is handsomely appointed with Polynesian-style furnishings, and a fully equipped kitchen, television/DVD, and washer/dryer. Nearby amenities include beach activities, golf, and restaurants.', '2009-03-24 13:18:16', '2009-03-24 13:18:16', 'MAUIBAYAN1.jpg', 'image/jpeg', 3380, '2009-03-24 13:18:15', 'MAUIBAYAN2.jpg', 'image/jpeg', 2805, '2009-03-24 13:18:14', 'MAUIBAYAN3.jpg', 'image/jpeg', 2698, '2009-03-24 13:18:14', 'MAUIBAYAN4.jpg', 'image/jpeg', 3763, '2009-03-24 13:18:14', 'MAUIBAYAN5.jpg', 'image/jpeg', 2539, '2009-03-24 13:18:15');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (738, 'Royal Solaris Los Cabos All Inclusive Resort & Spa', 5, 'This new deluxe "All Inclusive" resort offers a great variety of services. The modern rooms are elegantly furnished and decorated in a "Mexican Theme" within a majestic architectural design. The Royal Solaris is a wonderful value and is a excellent vacation spot for families, groups and individuals. Rooms are available in Ocean, and Garden View. Connecting rooms also available upon request and subject to availability at check in. Try the Royal Solaris and enjoy the many pleasures of Los Cabos!', '2009-03-24 18:31:41', '2009-03-24 18:31:41', 'solaris1.jpg', 'image/jpeg', 40552, '2009-03-24 18:31:41', 'solaris2.jpg', 'image/jpeg', 36624, '2009-03-24 18:31:38', 'solaris3.jpg', 'image/jpeg', 20593, '2009-03-24 18:31:39', 'solaris4.jpg', 'image/jpeg', 118655, '2009-03-24 18:31:40', 'solaris5.jpg', 'image/jpeg', 81388, '2009-03-24 18:31:40');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (739, 'Festiva Resort', 9, 'Welcome to Festiva Resorts, a family of boutique interval ownership resorts located in some of the most sought after destinations in the United States and Caribbean. Since its formation in 2000, Festiva Resorts has become one of the most successful privately held vacation ownership companies in the country. In an industry where large, publicly held corporations have recently begun to dominate, Festiva has carved out a niche in a number of prime vacation markets. Festiva Resorts began with one resort and 500 members and has now grown to 24 resorts, four Cruise Club destinations and the Festiva Adventure Club with nearly 55,000 members overall.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (740, 'Los Abrigados Resort and Spa', 56, 'Sedona Vacation Club/Los Abrigados is situated amid 20 acres of lush landscaping at Los Abrigados Resort. Recreation enthusiasts have numerous choices for activity--tennis, Nautilus workouts, swimming, and aerobics. For the golfer, there are two full-size courses only seven miles away. If you enjoy fishing, cast your line for trout in Oak Creek. Save a day to tour the Sedona countryside. The resort is next door to the Tlaquepaque Art Village. For those with relaxation in mind, the resort offers a eucalyptus steam bath, a whirlpool, massages, facials, a tanning salon, and sauna.', '2009-03-24 18:47:29', '2009-07-08 11:48:10', 'los1.jpg', 'image/jpeg', 20294, '2009-03-24 18:47:29', 'los2.jpg', 'image/jpeg', 7648, '2009-03-24 18:47:27', 'los3.jpg', 'image/jpeg', 15495, '2009-03-24 18:47:27', 'los4.jpg', 'image/jpeg', 13574, '2009-03-24 18:47:27', 'los5.jpg', 'image/jpeg', 22238, '2009-03-24 18:47:28');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (741, 'Los Tules Resort', 3, 'Located on one of Puerto Vallartas finest beaches, this privately owned condominium resort is only 10 minutes from the airport. The lowrise condominiums are spread out over 32 acres of lushly gardened grounds. With seven swimming pools, five tennis courts, a membership at the Flamingos Golf Club to choose from for sports, or relax on the half mile beach out your front door.', '2009-03-24 18:57:22', '2009-03-24 18:57:22', 'lt1.jpg', 'image/jpeg', 15319, '2009-03-24 18:57:22', 'lt2.jpg', 'image/jpeg', 12669, '2009-03-24 18:57:20', 'lt3.jpg', 'image/jpeg', 13952, '2009-03-24 18:57:20', 'lt4.jpg', 'image/jpeg', 100755, '2009-03-24 18:57:21', 'lt5.jpg', 'image/jpeg', 89650, '2009-03-24 18:57:21');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (742, 'Casa Dorada Los Cabos', 5, 'The most anticipated new resort in vibrant Cabo San Lucas, Casa Dorada Los Cabos, is truly in the heart of it all. With an incomparable location on Medano Beach -- the best swimmable beach in Cabo, this luxurious all-suite Beach, Spa and Golf Resort is also just steps away from world-class dining, entertainment, and the marina. For an ultra-upscale shopping experience, the acclaimed Luxury Avenue is just around the corner featuring brands like Cartier, Coach, Chopard and many more.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (743, 'Fishermen''s Village', 4, 'Relax and enjoy, as you listen to the soft sound of the waves gently rolling in. Fishermen''s Village is simply elegant. The Mexican charm of this resort offers the perfect location for your long awaited Caribbean getaway. Each room is uniquely graced with original art, sculptures and an incomparable view of the lush jungle or turquoise waters of the neighboring Caribe Maya. Capture the natural tranquility of the white sand and translucent waters of the Caribbean at Playa del Carmen. Known throughout the world for its water sport activities, our warm translucent waters and offshore coral reef offer you the best in diving, snorkeling, parasailing, windsurfing, Golf, tennis and shopping. The barefoot charm of Playa del Carmen awaits you only minutes away. Come and experience the magnificence for yourself.', '2009-03-25 11:50:33', '2009-03-25 11:50:33', 'fisherman1.jpg', 'image/jpeg', 3330, '2009-03-25 11:50:33', 'fisherman2.jpg', 'image/jpeg', 2382, '2009-03-25 11:50:31', 'fisherman3.jpg', 'image/jpeg', 2766, '2009-03-25 11:50:31', 'fisherman4.jpg', 'image/jpeg', 3113, '2009-03-25 11:50:32', 'fisherman5.jpg', 'image/jpeg', 2627, '2009-03-25 11:50:32');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (744, 'Marriott Harbour Point', 15, 'Marriott''s Harbour Point offers two-bedroom, two-bath villas, complete with private balcony, all located within Shelter Cove. Recreational amenities include three Har-Tru (clay) lighted tennis courts, two swimming pools (one heated), a bocce court, a shuffleboard court, barbecue areas, fitness equipment, and a supervised activity program. Excellent shopping and dining are within walking distance at the Shelter Cove Marina.', '2009-03-25 12:25:17', '2009-03-25 12:25:17', 'marriotharbourpoint1.jpg', 'image/jpeg', 5527, '2009-03-25 12:25:17', 'marriotharbourpoint2.jpg', 'image/jpeg', 2934, '2009-03-25 12:25:16', 'marriotbarony3.jpg', 'image/jpeg', 3367, '2009-03-25 12:25:16', 'marriotharbourpoint4.jpg', 'image/jpeg', 3967, '2009-03-25 12:25:16', 'marriotharbourpoint5.jpg', 'image/jpeg', 3140, '2009-03-25 12:25:17');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (745, 'Blue Bay', 5, 'The Blue Bay Getaway & Spa is the perfect place for couples and singles. After a multimillion dollar renovation this adult only resort now features new 5 star spa with steam room, hot tub, sauna, new restaurants, bars, and swimming pools. Adults only (18 years+) play and sun on a pristine white sand beach set amidst tropical gardens with 2 spectacular freeform pools, one with four whirlpools in a sexy atmosphere the other with three set in a more relaxing environment, all with swim-up bars.', '2009-03-25 12:31:50', '2009-03-25 12:31:50', 'bluebay1.jpg', 'image/jpeg', 2935, '2009-03-25 12:31:50', 'bluebay2.jpg', 'image/jpeg', 3320, '2009-03-25 12:31:48', 'bluebay2.jpg', 'image/jpeg', 3320, '2009-03-25 12:31:48', 'bluebay3.jpg', 'image/jpeg', 2517, '2009-03-25 12:31:49', 'bluebay3.jpg', 'image/jpeg', 2517, '2009-03-25 12:31:49');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (746, 'Raintree Vacation Club', 3, 'Raintree''s Club Regina is a comfortable property offering spectacular views of the Caribbean''s clear turquoise waters, white sandy beaches, and the Nichupte lagoon. The resort features beautiful units and amenities at the adjacent hotel including swimming pools, restaurants, bars, a gym, and a fitness center.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (747, 'Lawrence Welk Resort Villas', 20, 'Located about 30 miles north of San Diego, Lawrence Welk Resort Villas offers an outstanding variety of diversions for your daytime or nighttime pleasure. Golfers will be in heaven with two 18-hole courses on site, plus more than 70 additional courses within a half-hour drive.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (748, 'Hacienda del Mar Resort', 5, 'This magnificent resort is located in the mystical desert landscape of the Baja Peninsula. Surrounding the resort are golf courses that will challenge any handicap, or you can explore the peninsula, known for its beauty and endless photographic opportunities.', '2009-03-27 12:02:49', '2009-03-27 12:02:49', 'hdm1.jpg', 'image/jpeg', 5584, '2009-03-27 12:02:48', 'hdm2.jpg', 'image/jpeg', 2854, '2009-03-27 12:02:47', 'hdm3.jpg', 'image/jpeg', 3881, '2009-03-27 12:02:47', 'hdm4.jpg', 'image/jpeg', 4637, '2009-03-27 12:02:48', 'hdm5.jpg', 'image/jpeg', 4604, '2009-03-27 12:02:48');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (749, 'Polynesian Isles Resort, Kissimmee', 61, 'Polynesian Isles offers luxurious condominiums surrounded by an exotic tropical setting including a waterfall and lush vegetation. The resort has two swimming pools, four outdoor spas, tennis courts, basketball court, shuffleboard courts, fitness center, barbecue grills, children*s playground, game room, business center, and putting green. The resort is located minutes from the many theme parks, including Magic Kingdom®, Disney-MGM Studios, EPCOT®, and Disney\'s Animal Kingdom&reg...', '2009-03-27 17:18:18', '2009-03-27 17:18:18', 'poly1.jpg', 'image/jpeg', 77709, '2009-03-27 17:18:18', 'poly2.jpg', 'image/jpeg', 30134, '2009-03-27 17:18:16', 'poly3.jpg', 'image/jpeg', 37497, '2009-03-27 17:18:17', 'poly4.jpg', 'image/jpeg', 68003, '2009-03-27 17:18:17', 'poly5.jpg', 'image/jpeg', 46214, '2009-03-27 17:18:17');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (750, 'Avalon Grand, Cancun', 4, 'Avalon Grand is located in the heart of Cancun''s hotel zone. All the rooms overlook the Caribbean Sea and/or the beautiful Nichupte Lagoon. The Avalon Grand, is a five star hotel with the All Inclusive concept. This is a sophisticated projection of contemporary style with a touch of mediterranean details. Guests will enjoy two swimming pools, three outdoor whirlpools, and the excellent tastes of Bellavista Restaurant''s international cuisine. The Pool and Beach alone will be enough to relax your worries away while you lie in a warm blanket of the Mexican sun. Whether you decide to lay out by the pool, swim in the ocean, splash with the watersports, or have a deep-tissue massage in the spa, you will be worry free at the Avalon Grand.', '2009-03-30 11:52:06', '2009-03-30 11:52:06', 'avalon1.jpg', 'image/jpeg', 12042, '2009-03-30 11:52:06', 'avalon2.jpg', 'image/jpeg', 8797, '2009-03-30 11:52:04', 'avalon3.JPG', 'image/jpeg', 30654, '2009-03-30 11:52:05', 'avalon4.JPG', 'image/jpeg', 43668, '2009-03-30 11:52:05', 'avalon5.JPG', 'image/jpeg', 30315, '2009-03-30 11:52:06');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (751, 'Banyan Harbor', 28, 'Banyan Harbor, which is located only two miles from the Lihue Airport, is centrally located on the island of Kauai. The resort invites you to enjoy the on-site pool, tennis, shuffleboard, and barbecue grills. A short five-minute walk will take you to Kalapaki Beach, shopping areas, dining, and other activities. The Banyan Harbor''s location is within two miles of Kauai''s largest shopping center, three golf courses, theaters, cinemas, and Kauai''s business center.', '2009-03-30 12:20:05', '2009-03-30 12:20:05', 'banyanharbour1.jpg', 'image/jpeg', 11624, '2009-03-30 12:20:04', 'banyanharbour2.jpg', 'image/jpeg', 14894, '2009-03-30 12:20:02', 'banyanharbour3.jpg', 'image/jpeg', 3932, '2009-03-30 12:20:03', 'banyanharbour4.jpg', 'image/jpeg', 3318, '2009-03-30 12:20:04', 'banyanharbour5.jpg', 'image/jpeg', 5555, '2009-03-30 12:20:04');
