INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (756, 'The Pines at Treetop', 62, 'The Pines at Treetop Condominiums is located in the community of Four Seasons on Lake of the Ozarks. Nearby Lodge of Four Seasons is noted for its two 18-hole golf courses, a nine-hole executive course, four restaurants, a nightclub, boat rentals, and marina. Boating and water sports on the lake will fill your vacation days with sun and family fun.', '2009-04-01 16:04:32', '2009-04-01 16:04:32', 'pines1.jpg', 'image/jpeg', 41240, '2009-04-01 16:04:32', 'pines2.jpg', 'image/jpeg', 48252, '2009-04-01 16:04:30', 'pines2.jpg', 'image/jpeg', 48252, '2009-04-01 16:04:30', 'pines1.jpg', 'image/jpeg', 41240, '2009-04-01 16:04:31', 'pines1.jpg', 'image/jpeg', 41240, '2009-04-01 16:04:31');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (757, 'Club Bali Hai', 64, 'Club Bali Hai Moorea is situated on Cook''s Bay, one of the most beautiful inlets in the world. This resort offers a swimming pool, tennis court, and restaurant, open for breakfast and lunch. Beer and wine are also available during meals. Many restaurants are nearby for excellent dinners with free pickup. Rental car service and an activity desk allow fun outings, such as beach picnics, shark and sting ray feeding, snorkeling, sunset cruises, and photo safaris into the interior of the island. A rental car is recommended; special prices for club guests are available.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (758, 'Perennial Vacation Club', 37, 'As the eagles scan their domain, so will you enjoy the breathtaking view of the largest alpine lake in the Western Hemisphere. The snowcapped peaks of the Sierras beckon skiers and hikers alike. Challenge your angling skills on the lake with the feisty trout or test your luck on the green-clad tables of the famous casinos. Perennial Vacation at Eagles'' Nest''s distinctive European flavor is your mountain heaven.', '2009-04-02 13:59:02', '2009-04-02 13:59:02', 'perinal1.jpg', 'image/jpeg', 15821, '2009-04-02 13:59:02', 'perinal2.jpg', 'image/jpeg', 13569, '2009-04-02 13:59:00', 'perinal3.jpg', 'image/jpeg', 13646, '2009-04-02 13:59:00', 'perinal4.jpg', 'image/jpeg', 9561, '2009-04-02 13:59:01', 'perinal4.jpg', 'image/jpeg', 9561, '2009-04-02 13:59:01');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (759, 'Whiski Jack at the Snowbird', 52, 'Located in the fantastic Upper Village of Whistler, the resort is just minutes from the Blackcomb and Whistler ski areas. Winter snows provide hours of skiing enjoyment, both downhill and cross-country. Warm weather offers golf, tennis, horseback riding, and fishing close by, with skiing in the higher elevations. Take a hike or a bike ride along breathtaking mountain trails. In the evening, explore the many restaurants and nightclubs of Whistler or return to your vacation home for a cozy night by the fire. Underground parking, chains advised, oversize parking not within walking', '2009-04-02 14:59:07', '2009-04-02 14:59:07', 'snowbord1.jpg', 'image/jpeg', 3239, '2009-04-02 14:59:07', 'snowbord2.jpg', 'image/jpeg', 2678, '2009-04-02 14:59:05', 'snowbord3.jpg', 'image/jpeg', 4062, '2009-04-02 14:59:05', 'snowbord4.jpg', 'image/jpeg', 3016, '2009-04-02 14:59:06', 'snowbord5.jpg', 'image/jpeg', 2461, '2009-04-02 14:59:06');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (760, 'Whiski Jack at Town Plaza', 52, 'When the stress of life gets you down, find comfort and value at Whiski Jack at Whistler Town Plaza, Whistler. Timeshare homes like those offered at Whiski Jack at Whistler Town Plaza offer an ideal option for those seeking something larger than a hotel room to retreat to at the end of the day.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (761, 'Westgate South Beach', 61, 'Westgate South Beach is an oceanfront property nearby the shopping, nightlife and art galleries of Miami Beach. Guests are just two miles from the historical Art Deco District of Miami Beach, which has the largest concentration of 1920s and 1930s architecture in the world. The Lincoln Theatre, which houses the New World Symphony, and the Miami Beach Golf Course are one mile from the property. Those who want to get outside the city and closer to nature can explore the Everglades on a glass-bottom boat or airboat tour. Sporting bold geometric forms and pastel colors, the Westgate embraces the style of its Art Deco surroundings while offering unique details such as classically trimmed lamp stanchions and tropical landscaping. Guests at the Westgate South Beach can lounge by the resort''s heated outdoor pool or stroll along the sugar-white beach. The property also features volleyball courts and a children''s playground. Valet parking, beach service and a 24-hour front desk are ...', '2009-04-02 16:29:39', '2009-04-02 16:29:39', 'westgatesouthbeach1.jpg', 'image/jpeg', 4430, '2009-04-02 16:29:39', 'westgatesouthbeach2.jpg', 'image/jpeg', 3051, '2009-04-02 16:29:38', 'westgatesouthbeach3.jpg', 'image/jpeg', 3955, '2009-04-02 16:29:38', 'westgatesouthbeach4.jpg', 'image/jpeg', 2827, '2009-04-02 16:29:38', 'westgatesouthbeach5.jpg', 'image/jpeg', 2657, '2009-04-02 16:29:39');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (762, 'Westgate Park City Resort', 46, 'Located in The Canyons resort village, this picturesque ski-in/ski-out resort is the perfect setting for a ski vacation. The resort also has a restaurant, retail shops, indoor/outdoor pool, sauna and steam rooms, kids'' club, game room, fitness center, marketplace and deli, and a world-class spa. In-room amenities include a full kitchen, whirlpool tub, television, steam room, and fireplace in most units. Just 30 minutes'' drive from the Salt Lake City airport, Park City is a convenient and accessible ski resort area', '2009-04-02 16:42:44', '2009-04-02 16:42:44', 'westgatepark1.jpg', 'image/jpeg', 2371, '2009-04-02 16:42:43', 'westgatepark2.jpg', 'image/jpeg', 3189, '2009-04-02 16:42:41', 'westgatepark3.jpg', 'image/jpeg', 3505, '2009-04-02 16:42:42', 'westgatepark4.jpg', 'image/jpeg', 4349, '2009-04-02 16:42:42', 'westgatepark5.jpg', 'image/jpeg', 4225, '2009-04-02 16:42:43');
