INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (766, 'Grand Desert Resort', 37, 'Fairfield Grand Desert is located just minutes from the Las Vegas Strip where excitement never sleeps. Indulgence is the name of the Las Vegas game, and no matter how you play it, winning is a sure thing. Relaxing in high-roller style is always easy in Fairfield''s sumptuous suites. Viva la fun. Viva Las Vegas, baby. In order to accommodate both resort owners and daily guests at this resort, it may be necessary to make a room change during a guest stay. Resort will do everything possible to avoid this unlikely event, however, should the need arise to change a guest''s room, they will be advised at the time of checkin', '2009-04-02 17:04:09', '2009-04-02 17:04:09', 'granddesert1.jpg', 'image/jpeg', 4286, '2009-04-02 17:04:09', 'granddesert13.jpg', 'image/jpeg', 3875, '2009-04-02 17:04:07', 'granddesert2.jpg', 'image/jpeg', 3232, '2009-04-02 17:04:08', 'granddesert4.jpg', 'image/jpeg', 4179, '2009-04-02 17:04:08', 'granddesert5.jpg', 'image/jpeg', 2937, '2009-04-02 17:04:08');
