INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (768, 'WorldMark by Trend West Washington', 43, 'Welcome to the resort community known for its bratwurst, sauerkraut, and oompah-pah-WorldMark at Park Village. Tucked up next to the rugged Cascade Mountains, Leavenworth truly is an authentic Bavarian community, complete with European-style shops, beer gardens, and fine restaurants serving various European cuisine. There is plenty of snow in winter for Alpine and Nordic skiing, snowshoeing, snowmobiling, and ice fishing. In the summer, river rafting, kayaking, waterskiing, and canoeing take over on Lake Wenatchee and its tributary, the Wenatchee River.', '2009-04-03 13:12:05', '2009-04-24 13:09:00', 'worldmark1.jpg', 'image/jpeg', 4740, '2009-04-03 13:12:04', 'worldmark2.jpg', 'image/jpeg', 3137, '2009-04-03 13:12:03', 'worldmark2.jpg', 'image/jpeg', 3137, '2009-04-03 13:12:03', 'worldmark3.jpg', 'image/jpeg', 3871, '2009-04-03 13:12:04', 'worldmark3.jpg', 'image/jpeg', 3871, '2009-04-03 13:12:04');
