INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (770, 'Silver Lake Resort', 61, 'Get away from it all at this award-winning vacation retreat. Silver Lake Resort is adjacent to one of Florida''s most popular attractions--Walt Disney World(R). Free shuttle service is available to Universal Studios, SeaWorld, and Wet ''n Wild. The Caribbean ambience offers peace and quiet, and the location is perfect--in the heart of one of the country''s most popular vacation spots. A variety of restaurants and shops are a mile away. In addition to abundant off-site entertainment, a weekly schedule of on-site activities is centered around the spectacular heated outdoor pool and gorgeous clubhouse.', '2009-04-04 15:47:50', '2009-04-04 15:47:50', 'silverlake1.jpg', 'image/jpeg', 3649, '2009-04-04 15:47:50', 'silverlake2.jpg', 'image/jpeg', 3515, '2009-04-04 15:47:48', 'silverlake3.jpg', 'image/jpeg', 3533, '2009-04-04 15:47:49', 'silverlake4.jpg', 'image/jpeg', 3298, '2009-04-04 15:47:49', 'silverlake5.jpg', 'image/jpeg', 2167, '2009-04-04 15:47:49');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (771, 'San Luis Bay Inn', 20, 'Located in a small village on the California coast, San Luis Bay Inn Timeshare Resort offers the perfect family vacation. A swimming pool, tennis courts, and a golf course are just a few of the sports facilities available to you. Attractions close to the resort include Pismo Beach and the famous Hearst Castle. The area also boasts more than 30 wineries that you may tour. Fishing enthusiasts will enjoy Morro Bay, a quiet fishing village approximately 20 minutes north of the resort', '2009-04-04 15:52:20', '2009-04-04 15:52:20', 'sanluis1.jpg', 'image/jpeg', 56860, '2009-04-04 15:52:20', 'sanluis2.jpg', 'image/jpeg', 13933, '2009-04-04 15:52:18', 'sanluis3.jpg', 'image/jpeg', 12584, '2009-04-04 15:52:19', 'sanluis4.jpg', 'image/jpeg', 3822, '2009-04-04 15:52:19', 'sanluis5.jpg', 'image/jpeg', 3185, '2009-04-04 15:52:20');
