INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (774, 'Park Royal Resort,', 34, 'Park Royal Cozumel is an "All Inclusive" resort, presenting a 10 story building that features impressive Mayan Style stone work, as well as lush and expansive gardens that surround the building and the large, free-form swimming pool. A private underpass leads to a natural secluded beach, set up in a small cove. Located just 5 minutes from San Miguel downtown and just 10 minutes from Cozumel International airport. The Hotel is situated just in front of the largest reefs area. Park Royal Cozumel has 143 Rooms, 69 of them with king size beds and 74 have two twin beds. Most of the Rooms enjoy ocean view and the other Rooms have partial ocean / garden pool view. Each Room has individual control for the air conditioning, private balcony, satellite color TV and direct dialing telephone. ', '2009-04-05 14:32:08', '2009-04-05 14:32:08', 'parkroyal1.jpg', 'image/jpeg', 3475, '2009-04-05 14:32:08', 'parkroyal2.jpg', 'image/jpeg', 3004, '2009-04-05 14:32:06', 'parkroyal3.jpg', 'image/jpeg', 2172, '2009-04-05 14:32:07', 'parkroyal4.jpg', 'image/jpeg', 3183, '2009-04-05 14:32:07', 'parkroyal5.jpg', 'image/jpeg', 2436, '2009-04-05 14:32:07');
