INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (775, 'Plaza Holiday Club', 2, 'Treat yourself to the glitter, glamour, radiant sunshine, and glistening sands of Acapulco. The resort provides such amenities as outdoor swimming pools, tennis courts, seven restaurants, and three bars all located on the beach. The shopping, dining, and nightlife of Acapulco have drawn the jet-set for decades, come see why with a stay at the Plaza Hotel Club at Acapulco Playa Suites.', '2009-04-05 14:37:34', '2009-04-05 14:37:34', 'plazahoildayclub1.jpg', 'image/jpeg', 3952, '2009-04-05 14:37:34', 'plazahoildayclub2.jpg', 'image/jpeg', 2413, '2009-04-05 14:37:32', 'plazahoildayclub23.jpg', 'image/jpeg', 4368, '2009-04-05 14:37:32', 'plazahoildayclub4.jpg', 'image/jpeg', 4585, '2009-04-05 14:37:33', 'plazahoildayclub5.jpg', 'image/jpeg', 3457, '2009-04-05 14:37:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (776, 'Attitash Mountain Village', 67, 'Attitash Mountain Village is a four-season family resort nestled in the heart of New Hampshire''s White Mountains. The resort is across the street from Attitash/Bear Peak for world-class downhill skiing. A summer recreation area includes alpine and water slides, horseback riding, golf range, etc. On site, enjoy the indoor and outdoor pools and Jacuzzis, workout facility, tennis courts, hiking, biking, cross-country trails, and canoeing and fishing in the Saco River. Minutes away in North Conway, enjoy golf or tax-free outlet shopping.', '2009-04-06 13:09:48', '2009-04-06 13:09:48', 'attishmoutain1.jpg', 'image/jpeg', 3577, '2009-04-06 13:09:48', 'attishmoutain2.jpg', 'image/jpeg', 3537, '2009-04-06 13:09:46', 'attishmoutain3.jpg', 'image/jpeg', 3098, '2009-04-06 13:09:47', 'attishmoutain4.jpg', 'image/jpeg', 3446, '2009-04-06 13:09:47', 'attishmoutain5.jpg', 'image/jpeg', 2753, '2009-04-06 13:09:48');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (777, 'Krystal Cancun', 4, 'Krystal International Vacation Club Cancun offers the specialized attention all vacationers desire. The resort is located in Punta Cancun Beach, next to the turquoise waters of the Caribbean Sea. Enjoy tennis or aquatic activities and the outdoor pool.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (778, 'Allegro Resort, Playacarmen', 4, 'Soak in the sun by the outdoor swimming pool or enjoy the nearby beach. For some exercise, try the tennis courts located on site. Sample the authentic regional cuisine from the resort''s restaurant or go into the town of Playa del Carmen a relaxed beach town across from the coast of Cozumel', '2009-04-06 13:18:30', '2009-04-06 13:18:30', 'allegro1.jpg', 'image/jpeg', 1960, '2009-04-06 13:18:29', 'allegro2.jpg', 'image/jpeg', 6007, '2009-04-06 13:18:28', 'allegro3.jpg', 'image/jpeg', 4131, '2009-04-06 13:18:28', 'allegro4.jpg', 'image/jpeg', 2182, '2009-04-06 13:18:29', 'allegro5.jpg', 'image/jpeg', 1995, '2009-04-06 13:18:29');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (780, 'Royal Goan Beach Resort', 13, 'Located in the south of Goa, this resort is a spectacular development of apartments and villas, only 15 mins walk from the stunning beaches, which stretch for over 28 kms. The central feature is the swimming pool area with its poolside air conditioned Coconut Tree restaurant and bar. Other facilities include 24 hour reception, gynasium, daily activity program and live entertainment most evenings. Watersports and sightseeing trips available for a fee taking in temples and churches of Old Goa. Enjoy local shopping, including craft centres and spice farms.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (781, 'Sheraton''s Mountain Vista', 55, 'Sheraton''s Mountain Vista is conveniently located in the heart of the Vail Valley''s Avon/Beaver Creek Resort area. Within walking distance of restaurants and shopping, this resort is only a shuttle ride away from some of the area''s top slopes in America. Besides downhill skiing, winter activities include cross- country skiing and ice-skating. Summer brings activities such as golf, hiking, biking, and fishing. Amenities will include a health club, pool, and whirlpool tubs.', '2009-04-06 15:04:15', '2009-04-06 15:04:15', 'sheartonmoutainnista1.jpg', 'image/jpeg', 12873, '2009-04-06 15:04:15', 'sheartonmoutainnista2.jpg', 'image/jpeg', 12447, '2009-04-06 15:04:13', 'sheartonmoutainnista3.jpg', 'image/jpeg', 37028, '2009-04-06 15:04:13', 'sheartonmoutainnista4.jpg', 'image/jpeg', 33768, '2009-04-06 15:04:14', 'sheartonmoutainnista5.jpg', 'image/jpeg', 44129, '2009-04-06 15:04:14');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (782, 'Sunterra Resorts Sedona Summit', 56, 'Sedona Summit Resort is located in "the land of four mild seasons." At your leisure enjoy trout fishing in Oak Creek or exploring Oak Creek Canyon. Jeep tours of Oak Creek Canyon are also available for a fee. Sedona''s many offerings include the Tlaquepaque Art Village featuring acclaimed Western artists, an abundance of American Indian arts and crafts (because of the city''s proximity to Navajo, Hopi), and other reservations, galleries, restaurants, and shops. Relax by the resort''s pool after all of the sight- seeing this area offers.', '2009-04-06 15:10:39', '2009-04-06 15:10:39', 'sedonasummit1.jpg', 'image/jpeg', 4161, '2009-04-06 15:10:38', 'sedonasummit2.jpg', 'image/jpeg', 3186, '2009-04-06 15:10:37', 'sedonasummit3.jpg', 'image/jpeg', 3511, '2009-04-06 15:10:37', 'sedonasummit4.jpg', 'image/jpeg', 3186, '2009-04-06 15:10:38', 'sedonasummit5.jpg', 'image/jpeg', 3770, '2009-04-06 15:10:38');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (783, 'Canto del Sol Plaza Vallarta', 3, 'Puerto Vallarta is divided by the Cuale River. It has streets that rise up to the summit of the hills; at the sides, red- roofed houses and modern hotels punctuate the scene. In Puerto Vallarta, the Costa Alegre begins where the majestic sea, the beautiful beaches, the exotic flowers, and the rivers flow into the ocean', '2009-04-06 15:26:16', '2009-04-06 15:26:16', 'cantodelsol1.jpg', 'image/jpeg', 3295, '2009-04-06 15:26:15', 'cantodelsol2.jpg', 'image/jpeg', 4351, '2009-04-06 15:26:14', 'cantodelsol3.jpg', 'image/jpeg', 5026, '2009-04-06 15:26:14', 'cantodelsol4.jpg', 'image/jpeg', 2944, '2009-04-06 15:26:14', 'cantodelsol5.jpg', 'image/jpeg', 3474, '2009-04-06 15:26:15');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (784, 'Rancho Banderas Vacation Villas', 1, ' Rancho Banderas Vacation Villas is located on a stretch of white, sandy beach. The appeal of this self-contained, family-oriented resort is enhanced by the abundance of recreational activities available. Rancho Banderas combines the beauty of Mexican colonial architecture with comfortable accommodations.', '2009-04-06 15:31:27', '2009-04-06 15:31:27', 'rancho1.jpg', 'image/jpeg', 41659, '2009-04-06 15:31:26', 'rancho2.jpg', 'image/jpeg', 46371, '2009-04-06 15:31:25', 'rancho3.jpg', 'image/jpeg', 52177, '2009-04-06 15:31:25', 'rancho4.jpg', 'image/jpeg', 54257, '2009-04-06 15:31:25', 'rancho5.jpg', 'image/jpeg', 55381, '2009-04-06 15:31:26');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (785, 'Fiesta Americana Vacation Club at Cabo del Sol', 5, 'Fiesta Americana Vacation Club is perched on 27 acres of dramatic hillside and ocean front porperty in Cabo San Lucas. FAVClub offers breathtaking landscape for a memo- rable vacation in Mexico. In addition to 150 beautifully appointed villas, the rst features a full service spa, a convention center with 20,000 sq feet of meeting spac e, 3 distinctive restaurants on-site, 24hr room service, tennis courts and 2 swimming pools. Members are entitled to preferred privileges at the 18 hole golf course, signature of Jack Nicklaus, located at Cabo del Sol property.', '2009-04-06 15:38:48', '2009-04-06 15:38:48', 'fa1.jpg', 'image/jpeg', 3975, '2009-04-06 15:38:47', 'fa2.jpg', 'image/jpeg', 2566, '2009-04-06 15:38:46', 'fa3.jpg', 'image/jpeg', 2881, '2009-04-06 15:38:46', 'fa4.jpg', 'image/jpeg', 3772, '2009-04-06 15:38:47', 'fa5.jpg', 'image/jpeg', 3105, '2009-04-06 15:38:47');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (786, 'Vacation Village at Parkway', 61, 'Vacation Village at Parkway offers the best of Florida: great entertainment, fabulous shopping, thrilling recreation, and mouthwatering restaurants. The resort is less than five minutes from the Magic Kingdom''s main gate and offers lovely accommodations in stylish West Indies decor. Your stay here puts you in the center of Orlando''s many attractions such as Epcot, SeaWorld, Universal Studios, and Pleasure Island. Visit historic St. Augustine, the Kennedy Space Center, Busch Gardens, and Silver Springs--all within a short driving distance.', '2009-04-09 16:22:22', '2009-04-09 16:22:22', 'vacationvillageparkway1.jpg', 'image/jpeg', 75287, '2009-04-09 16:22:21', 'vacationvillageparkway2.jpg', 'image/jpeg', 12698, '2009-04-09 16:22:20', 'vacationvillageparkway2.jpg', 'image/jpeg', 12698, '2009-04-09 16:22:20', 'vacationvillageparkway3.jpg', 'image/jpeg', 19413, '2009-04-09 16:22:20', 'vacationvillageparkway3.jpg', 'image/jpeg', 19413, '2009-04-09 16:22:21');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (787, 'Grand Pacific MarBrisa Resort', 20, 'Located on more than forty acres overlooking the Pacific Ocean, this incredible Spanish Mediterranean-style resort offers luxurious studio, one, two and three bedroom vacation villas in the midst of Southern California''s most popular attractions! The freeform swimming pool offers a paradise of fun and relaxation with hot tubs and elevated sundecks. Other planned amenities include a health club, sauna, horseback riding, barbeque area, and playground for the kids. Nearby, enjoy golfing, the beach, scuba diving, snorkeling, wind surfing, boating, water skiing, a spa, shopping, dining, and live entertainment. This property is affiliated with the Grand Pacific and Hilton Grand Vacations Family of resorts.', '2009-04-14 15:20:41', '2009-04-14 15:20:41', 'marbrisa1.jpg', 'image/jpeg', 195380, '2009-04-14 15:20:41', 'marbrisa2.jpg', 'image/jpeg', 164098, '2009-04-14 15:20:39', 'marbrisa3.jpg', 'image/jpeg', 130941, '2009-04-14 15:20:39', 'marbrisa4.jpg', 'image/jpeg', 131738, '2009-04-14 15:20:40', 'marbrisa5.jpg', 'image/jpeg', 197889, '2009-04-14 15:20:40');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (788, '  Global Discovery Travel', 61, 'THE FOLLOWING IS A PARTIAL list of the resorts our members have vacationed or are available to you. Please DO NOT consider this list as a limitation on your choices it is provided only so you may see samples of the condominium you have available or access to. Unfortunately, all condominiums do not have website or brochures in this case your travel consultant will provide you with as much information as possible to allow you to make your decision. Condominiums that appear on this list are not always available or may no longer be used with your program.', '2009-04-15 13:02:03', '2009-04-15 13:02:03', 'global1.jpg', 'image/jpeg', 38994, '2009-04-15 13:02:03', 'global2.jpg', 'image/jpeg', 21539, '2009-04-15 13:02:02', 'global3.jpg', 'image/jpeg', 23853, '2009-04-15 13:02:02', 'global4.jpg', 'image/jpeg', 20477, '2009-04-15 13:02:02', 'global5.jpg', 'image/jpeg', 20477, '2009-04-15 13:02:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (789, 'Costa Sur', 3, 'Costa Sur offers another choice for anyone who loves Puerto Vallarta and wants to explore one of the most beautiful areas of Banderas Bay. The resort\'s location is at the gateway to the beautiful south shore where jungle covered mountains plunge directly into the Bay. The coast road leads to Mismaloya where  \"The Night of the Iguana\" was filmed and then to Boca de Tomatlan, a small fishing village where you can take a power boat to any of the beautiful beaches in the roadless area along the southern edge of the Bay', '2009-04-15 13:48:32', '2009-04-15 13:48:32', 'costa1.jpg', 'image/jpeg', 38388, '2009-04-15 13:48:31', 'costa2.jpg', 'image/jpeg', 44706, '2009-04-15 13:48:30', 'costa3.jpg', 'image/jpeg', 37149, '2009-04-15 13:48:30', 'costa4.jpg', 'image/jpeg', 48556, '2009-04-15 13:48:30', 'costa5.jpg', 'image/jpeg', 4571, '2009-04-15 13:48:31');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (790, 'Marriott''s StreamSide', 55, 'Vail''s international village and ski slopes can easily be reached by StreamSide shuttle service. The Aspen, Birch, and Cedar buildings offer studio to two-bedroom accommodations. On-site recreation includes an indoor/outdoor pool, whirlpool spas, sauna, steam room, and racquetball courts. These classic buildings offer a rustic charm. Each one''s style, decor, and unit size varies. Cedar''s exterior and Birch''s and Aspen''s interiors are shown. Aspen and Cedar have stair access only (no elevators). StreamSide at Vail does not have air conditioning in any guest rooms or public areas', '2009-04-15 13:54:35', '2009-04-15 13:54:35', 'marriottsteamside1.jpg', 'image/jpeg', 5866, '2009-04-15 13:54:34', 'marriottsteamside2.jpg', 'image/jpeg', 3437, '2009-04-15 13:54:33', 'marriottsteamside3.jpg', 'image/jpeg', 2886, '2009-04-15 13:54:33', 'marriottsteamside4.jpg', 'image/jpeg', 2889, '2009-04-15 13:54:34', 'marriottsteamside5.jpg', 'image/jpeg', 3146, '2009-04-15 13:54:34');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (791, 'Ka''anapali Beach Club', 28, 'At the north end of Maui''s world-famous Kaanapali Beach, the resort offers sumptuous landscaped grounds right on the beach. Each unit at the resort offers a spacious lanai. One of the highlights at the resort is a one-acre swimming pool with a water slide. The resort also offers a variety of on-site dining choices. Within minutes of the resort, you will find five championship golf courses, tennis facilities, and international shopping retailers. The resort is located five miles from the old whaling capital of Lahaina. The resort also offers miniature golf and children''s program.', '2009-04-15 13:59:29', '2009-04-15 13:59:29', 'kaanapali1.jpg', 'image/jpeg', 2357, '2009-04-15 13:59:29', 'kaanapali2.jpg', 'image/jpeg', 3561, '2009-04-15 13:59:27', 'kaanapali3.jpg', 'image/jpeg', 4365, '2009-04-15 13:59:28', 'kaanapali4.jpg', 'image/jpeg', 3409, '2009-04-15 13:59:28', 'kaanapali5.jpg', 'image/jpeg', 2753, '2009-04-15 13:59:28');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (792, 'Eagle Trace at Massanutten', 24, 'A tradition of excellence continues at Eagle Trace at Massanutten. With Virginia''s only quad chairlift and a vertical drop from 795 to 1,110 feet, Massanutten offers a thrilling challenge for downhill skiers and snowboarders. Also on site, enjoy two extensive sports complexes with an indoor pool, gymnasium, racquetball courts, exercise equipment, steam room, sauna, massage services, and children''s programs. In summer, enjoy the outdoor pool and tennis courts. The golf pro shop offers an 18-hole, PGA-rated or nine-hole Executive golf course, a putting green, and driving range.', '2009-04-15 14:07:33', '2009-04-15 14:07:33', 'eagletrace1.jpg', 'image/jpeg', 3791, '2009-04-15 14:07:33', 'eagletrace2.jpg', 'image/jpeg', 4136, '2009-04-15 14:07:32', 'eagletrace3.jpg', 'image/jpeg', 3185, '2009-04-15 14:07:32', 'eagletrace4.jpg', 'image/jpeg', 3706, '2009-04-15 14:07:32', 'eagletrace5.jpg', 'image/jpeg', 2937, '2009-04-15 14:07:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (793, 'Omni Cancun Hotel and Villas', 4, 'Omni Cancun Hotel and Villas is situated on Cancun''s best stretch of white, powdery beach, overlooking the Caribbean Sea and tranquil Nichupte Lagoon. Cascading swimming pools surrounded by lush tropical gardens, complemented by true Mexican architecture, murals, and stone reliefs add special ambiance to the resort. Theme restaurants include La Paloma, serving Mexican cuisine; the Pina Colada beach bistro; and Da Vinci''s fine dining. The swim-up bar and whirlpool beach bar, Kuku''s, satisfy your thirst for fun. An attentive staff awaits the chance to welcome you to "su casa."', '2009-04-15 14:17:40', '2009-04-15 14:17:40', 'omni1.jpg', 'image/jpeg', 4150, '2009-04-15 14:17:39', 'omni2.jpg', 'image/jpeg', 5148, '2009-04-15 14:17:38', 'omni3.jpg', 'image/jpeg', 6047, '2009-04-15 14:17:38', 'omni4.jpg', 'image/jpeg', 4225, '2009-04-15 14:17:38', 'omni5.jpg', 'image/jpeg', 3153, '2009-04-15 14:17:39');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (794, 'Marriott''s Ocean Pointe', 61, 'Situated on Florida''s Palm Beach Shores, Marriott''s Ocean Pointe offers two swimming pools with whirlpool spas, a children''s pool, tennis courts, health club, a playground, outdoor bar and grill, and The MarketPlace convenience store and deli. Golf, tennis, boating, diving, snorkeling, and world-class fishing are all nearby, as are premium retail shops on Palm Beach''s legendary Worth Avenue. Each one-, two-, and three-bedroom villa includes an oceanfront or oceanside view, a whirlpool spa in the master bath, a fully equipped kitchen, utility room with washer/dryer, and living and dining areas.', '2009-04-15 16:05:19', '2009-04-15 16:05:19', 'marriottoceanpointe1.jpg', 'image/jpeg', 3703, '2009-04-15 16:05:18', 'marriottoceanpointe2.jpg', 'image/jpeg', 3336, '2009-04-15 16:05:17', 'marriottoceanpointe3.jpg', 'image/jpeg', 3829, '2009-04-15 16:05:17', 'marriottoceanpointe4.jpg', 'image/jpeg', 3587, '2009-04-15 16:05:17', 'marriottoceanpointe5.jpg', 'image/jpeg', 2791, '2009-04-15 16:05:18');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (804, 'The Westin Desert Willow', 20, 'Discover the essence of desert tranquility amid an inspiring outcropping of the Santa Rosa Mountains in southern California. This uniquely imagined resort is suffused with signature Westin style and personal services to help you relax, refresh and renew.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (795, 'Alhambra at Poinciana', 61, 'Experience the true meaning of "vacation" here, surrounded by sparkling blue lakes, quiet lazy creeks, romantic pathways, and stands of beautiful oak, pine and palmetto trees that sway in the breeze.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (797, 'The Ridge Tahoe', 37, 'Six miles from Lake Tahoe, this resort offers dramatic views from its perch on a ridge, looking down on Nevada''s sprawling Carson Valley.
