INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (799, 'Elkhorn Resort', 42, 'Elkhorn Resort encompasses 160 acres bordering Riding Mountain National Park. This four season area is a haven for mountain biking, swimming & encountering wildlife. Experience a hike on one of the parks 57 trails, or enjoy watersport activities on beautiful Clear Lake. Tour the beauty of Elkhorn on horseback. Catch the stagecoach for an enjoyable ride through Wasagaming. Shops are open from May to October. Don''t forget to golf one of Canada''s premier golf courses only 5kms away. In the winter months enjoy cross country & alpine skiing, the tobaggan hill & sleigh rides- great family activities.', '2009-04-21 12:17:13', '2009-04-21 12:17:13', 'elkhorn11.jpg', 'image/jpeg', 14164, '2009-04-21 12:17:13', 'elkhorn22.jpg', 'image/jpeg', 15103, '2009-04-21 12:17:11', 'elkhorn4.jpg', 'image/jpeg', 26383, '2009-04-21 12:17:12', 'elkhorn3.jpg', 'image/jpeg', 19448, '2009-04-21 12:17:12', 'elkhorn5.jpg', 'image/jpeg', 13730, '2009-04-21 12:17:13');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (800, 'Law Cranberry Resort Limited', 49, 'Law Cranberry Resort Limited is located in Collingwood, Ontario in Canada and makes for a great vacation getaway. By renting a condo directly from an owner at Law Cranberry Resort Limited, which is a resort condominium, you will find your best vacation value! Law Cranberry Resort Limited offers a swimming pool and is near restaurants and shops in the Collingwood, Ontario area for your entertainment. Make Law Cranberry Resort Limited your Canada vacation getaway!
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (801, 'Ocean East Resort Club', 61, 'This professionally managed resort is brought to you by Endless Vacation Rentals. Enjoy an endless array of activities at the family-oriented Ocean East Resort Club in Ormond Beach. The on-site recreational director can help you plan activities or provide sports equipment for the days events. Take advantage of the resorts heated outdoor pool and recreation room with Ping-Pong table, board games, library, TV/VCR, and video games room or take a leisurely stroll on the beach.', '2009-04-21 12:35:34', '2009-04-21 12:35:34', 'ormand1.jpg', 'image/jpeg', 20842, '2009-04-21 12:35:33', 'ormand2.jpg', 'image/jpeg', 27063, '2009-04-21 12:35:32', 'ormand3.jpg', 'image/jpeg', 11233, '2009-04-21 12:35:32', 'ormand4.jpg', 'image/jpeg', 12091, '2009-04-21 12:35:33', 'ormand4.jpg', 'image/jpeg', 12091, '2009-04-21 12:35:33');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (802, 'Finisterra Hotel', 5, 'The incredible natural beauty and a unique location of Cabo San Lucas combine to make the Finisterra Hotel an unforgettable choice for your Cabo San Lucas getaway.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (803, 'The Westin Mission Hills', 20, 'Set on 360 acres within the Rancho Mirage community, The Westin Mission Hills Resort & Spa is surrounded by landscaped courtyards and extensive waterways. Guests enjoy elegant accommodations, ample meeting space, and a relaxing desert climate.', '2009-04-21 13:01:15', '2009-04-21 13:01:15', 'westinhills1.jpg', 'image/jpeg', 18929, '2009-04-21 13:01:14', 'westinhills2.jpg', 'image/jpeg', 35868, '2009-04-21 13:01:12', 'westinhills3.jpg', 'image/jpeg', 31142, '2009-04-21 13:01:12', 'westinhills4.jpg', 'image/jpeg', 31178, '2009-04-21 13:01:13', 'westinhills5.jpg', 'image/jpeg', 18442, '2009-04-21 13:01:14');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (890, 'Eagle Crest Resort', 39, 'Eagle Crest is a full-service destination resort located just outside the city of Redmond on 1700 acres in the high desert of Central Oregon. Nestled against the majestic Cascade Mountains near Bend, Oregon, Eagle Crest boasts over 300 days of sunshine each year with an annual rainfall of less than nine inches.', '2009-09-17 20:26:03', '2009-09-17 20:26:03', 'ea1.jpeg', 'image/jpeg', 12376, '2009-09-17 20:26:03', 'ea2.jpeg', 'image/jpeg', 7626, '2009-09-17 20:26:01', 'ea3.jpeg', 'image/jpeg', 8311, '2009-09-17 20:26:02', 'ea4.jpeg', 'image/jpeg', 31023, '2009-09-17 20:26:02', 'ea5.jpeg', 'image/jpeg', 16625, '2009-09-17 20:26:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (805, 'Cliffs at Peace Canyon', 56, 'Vacationing at The Cliffs at Peace Canyon affords you and your family the ultimate in entertainment. From spine-tingling volcanic eruptions to pirate battle reenactments, the Las Vegas Strip and surrounding area offer entertainment opportunities for one and all. Las Vegas is unsurpassed when it comes to spellbinding stage shows and extraordinary entertainment, too. And there are slot machines, high stakes table games, blackjack, craps, roulette and even the very latest in reel and video gaming, too.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (806, 'Royal Goan Beach Club at Benaulim', 73, 'Situated in the heart of Benaulim lies the quaint and cozy Beach Club. This exclusive members club sports large apartments in an enclosed arrangement that makes it feel like a holiday village. The apartments are tastefully decorated with a noticeable use of open space to help you relax in luxurious comfort.
