INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (830, 'Royal Resort', 37, 'Royal Resort offers all the intimacy and comforts of a smaller resort, but is located in the heart of the famous Las Vegas Strip, next to Wynn, and is within walking distance to the Las Vegas Convention Center. Enjoy the smoke-free lobby with spacious lounge, dine at Cosmo\'s Ristorante and splash around in the sparkling pool with private cabanas. ', '2009-04-28 13:05:19', '2009-04-28 13:05:19', 'rrlv1.jpg', 'image/jpeg', 42526, '2009-04-28 13:05:19', 'rrlv2.jpg', 'image/jpeg', 43867, '2009-04-28 13:05:17', 'rrlv3.jpg', 'image/jpeg', 48688, '2009-04-28 13:05:18', 'rrlv4.jpg', 'image/jpeg', 54215, '2009-04-28 13:05:18', 'rrlv5.jpg', 'image/jpeg', 47681, '2009-04-28 13:05:18');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (831, 'Luperon Beach Resort', 41, 'This hotel comprises a total of 320 rooms spread over 15 floors. Guests may make use of the foyer with 24-hour reception, currency exchange facility, hair dressing salon and various shops. Two bars, a nightclub and 3 air-conditioned i  la carte restaurants are also to be found. Furthermore, the hotel offers Internet access, a children''s play area and a kids'' club. Those arriving by car may take advantage of the hotel''s car park or garage facilities. A laundry service and medical assistance round off the facilities on offer.', '2009-04-29 11:13:46', '2009-04-29 11:13:46', 'luperondr1.jpeg', 'image/jpeg', 35539, '2009-04-29 11:13:46', 'luperondr2.jpeg', 'image/jpeg', 31589, '2009-04-29 11:13:44', 'luperondr3.jpeg', 'image/jpeg', 36425, '2009-04-29 11:13:45', 'luperondr4.jpeg', 'image/jpeg', 18333, '2009-04-29 11:13:45', 'luperondr5.jpeg', 'image/jpeg', 10210, '2009-04-29 11:13:45');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (832, 'Sheraton Broadway Plantation', 15, 'Framed by towering pine trees, Sheraton Broadway Plantation gives a feeling of seclusion yet puts you right in the heart of all the action. Across the street from famed Broadway at the Beach, this resort is just one mile from the ocean.Myrtle Beach offers a diverse array of activities for the whole family. Explore unspoiled beaches, amusement parks, water parks and state parks. Tee-up on world-class golf courses and be sure to save plenty of time for shopping.Within your spacious villa, you''ll find everything a resort home should have. All villas feature well equipped kitchens, and some have a whirlpool bath in the master suite and a screened terrace to help you unwind.', '2009-05-01 13:28:21', '2009-05-01 13:28:21', 'sbp1.jpg', 'image/jpeg', 30788, '2009-05-01 13:28:20', 'sbp2.jpg', 'image/jpeg', 2846, '2009-05-01 13:28:19', 'sbp3.jpg', 'image/jpeg', 31044, '2009-05-01 13:28:19', 'sbp4.jpg', 'image/jpeg', 30837, '2009-05-01 13:28:20', 'sbp.jpg', 'image/jpeg', 31285, '2009-05-01 13:28:20');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (833, 'Royal Aloha Vacation Club', 28, 'Almost all the attractions of Waikiki are within walking distance of the Royal Aloha Vacation Club/Waikiki, located in the heart of Honolulu. The resort offers an outdoor pool, sauna, barbecue areas, and paddle tennis courts. The beach is only three blocks away. A number of inter- national plazas, marketplaces, shopping centers, and bazaars can be found nearby. Don''t miss the sight-seeing attractions on Oahu, including Diamond Head State Park, the Kodak Hula Show, the Royal Hawaiian Art Gallery, and Honolulu Zoo. The city also offers many restaurants, that specialize in a number of cuisines.', '2009-05-01 15:27:11', '2009-05-01 15:27:11', 'ravc1.jpg', 'image/jpeg', 11978, '2009-05-01 15:27:10', 'ravc2.jpg', 'image/jpeg', 11925, '2009-05-01 15:27:09', 'ravc3.jpg', 'image/jpeg', 7904, '2009-05-01 15:27:09', 'ravc4.jpg', 'image/jpeg', 7739, '2009-05-01 15:27:09', 'ravc5.jpg', 'image/jpeg', 6970, '2009-05-01 15:27:10');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (834, 'The Quarter House', 47, 'New Orleans'' French Quarter has The Quarter House timeshare resort, a luxury suite resort in the heart of the Vieux Carre. The building was designed by renowned New Orleans architect James Gallier in 1831. In 1983, the New Orleans timeshare resort underwent historical renovation. Enjoy all of the Louisiana timeshare resort''s atmosphere, the French Quarter, Aquarium of the Americas, and many historic buildings and museums. Walk along the Mississippi River or enjoy exciting nightlife and an evening cruise. Resort amenities include a tropical courtyard with a wading pool.', '2009-05-01 15:34:28', '2009-05-01 15:34:28', 'QHouse1.jpg', 'image/jpeg', 17350, '2009-05-01 15:34:27', 'QHouse2.jpg', 'image/jpeg', 17350, '2009-05-01 15:34:26', 'QHouse3.jpg', 'image/jpeg', 17350, '2009-05-01 15:34:26', 'QHouse4.jpg', 'image/jpeg', 3341, '2009-05-01 15:34:26', 'QHouse5.jpg', 'image/jpeg', 15952, '2009-05-01 15:34:27');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (835, 'Oyster Bay Beach Resort', 9, 'At Oyster Bay, you can embrace adventure through fun on-site activities ,  or just find your own special place to sink into serenity. And there are no limits to what you can see and do elsewhere on the island. We are just minutes away from the hottest nightlife and shopping found in Philipsburg; the famous Orient Beach; and Grand Case, the gourmet capital of the Caribbean. With the help of our friendly activity desk staff, you can plan any number of extraordinary excursions. Take a closer look at Oyster Bay and discover why this is where you want to be.', '2009-05-04 11:54:08', '2009-05-04 11:54:08', 'oysterb1.jpg', 'image/jpeg', 23461, '2009-05-04 11:54:07', 'oysterb2.jpg', 'image/jpeg', 14912, '2009-05-04 11:54:06', 'oysterb3.jpg', 'image/jpeg', 18137, '2009-05-04 11:54:06', 'oysterb4.jpg', 'image/jpeg', 25739, '2009-05-04 11:54:07', 'oysterb5.jpg', 'image/jpeg', 24300, '2009-05-04 11:54:07');
