INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (843, 'Wyndham Palm Springs', 20, 'Please note: The Wyndham Palm Springs will be under renovation May 1 - Dec 30, 2009. During this time, the hotel will remain open. The Restaurant will be out of service from May 26 - August 15th, 2009 and will be serving meals in our lobby lounge area. The Lobby will be out of service from Sept 1 - November 30th. We will be utilizing renovated meeting space for our check-in and meal areas.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (844, 'Shadow Ridge Hotel', 66, 'This place has a great location and is the best lodging for the money we have run across in Park City. We''ve been going to PC for 20 years to ski, golf, hike, and mountain bike. We wish we had tried Shadow Ridge much earlier.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (845, 'Wyndham Flagstaff', 56, 'When a special occasion is on the horizon, find comfort and value at Wyndham Flagstaff (formerly Fairfield), Flagstaff. Timeshare vacation rentals like the ones at Wyndham Flagstaff (formerly Fairfield) provide a high-quality alternative to motel rooms.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (846, 'The Grand Regina Villa', 5, 'The Grand Regina Villa rentals are as beautiful as their pristine beaches. Book a vacation with your family, your friends, or just you and your loved one. Imagine your own private villa rental overlooking the Baja California Peninsula and Sea of Cortez. A dream Los Cabos vacation rental can''t get better than this. This is your moment. Your much deserved Cabo San Lucas villa vacation is within reach!
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (847, 'Royal Islander', 4, 'The Royal Islander is situated on Cancun''s finest beach, directly facing the turquoise blue Caribbean. Many amenities are available at this private exclusive resort, including swimming pools, tennis courts, bicycles, restaurant, a mini-market, and full maid service. A full marina exists opposite on the lagoon, with sailing, fishing, windsurfing, waterskiing, and jet skiing. There is a comprehensive activities program on-site, with excursions to Tulum, Isla Mujeres, and the pyramids of Chichen Itza and Coba. All villas have large sea-view balconies, marble-floored interiors, and fully equipped kitchens.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (848, 'Hona Koa Resort', 28, 'In every way, a Maui Vacation is a family-bonding experience.  From volcanoes to rain forests to beaches, experience the beauty of Maui''s landscape.  The Magic Isles  are an adventurer''s delight. Here you can immerse yourself in activities that accommodate visitors with all levels of experience and physical ability. The emphasis on fun, and education, all provided by an unbeatable selection of outdoor activities, world-class attractions, resort programs, great activities and Native Hawaiian Cultural adventures!
