INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (864, 'Elysian Beach Resort', 7, 'Enclosed by palm-shaded hills and overlooking its own yacht-filled cove, Elysian Beach Resort offers a most magnificent setting. The resort has 67 villas, each with a balcony that overlooks the sea. In addition to its own white-sandy beach (what else do you really need?), this resort has amenities to please everyone, including tennis, a fitness club, swimming, sailing and snorkeling. Take a dip in the pool, or just relax with a good book under the palm trees.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (865, 'Grand Caymanian', 27, 'The New Grand Caymanian Resort is truly a beautiful property.  Secluded yet close to all the shopping and restaurants, it offers the perfect balance between creature comforts and "the Island time forgot".  A huge private beach with overhanging palm trees will take you far away from the fast paced environment you''ve come to escape.', '2009-05-26 15:06:05', '2009-05-26 15:06:05', 'cay1.jpg', 'image/jpeg', 222530, '2009-05-26 15:06:04', 'cay2.JPG', 'image/jpeg', 140188, '2009-05-26 15:06:01', 'cay3.JPG', 'image/jpeg', 98686, '2009-05-26 15:06:02', 'cay4.JPG', 'image/jpeg', 146497, '2009-05-26 15:06:03', 'cay2.JPG', 'image/jpeg', 140188, '2009-05-26 15:06:03');
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (866, 'Monarch Grand Vacations Las Vegas', 37, 'Guests of the Cancun Resort in Las Vegas won''t have any problem finding plenty to see and do. In fact, don''t be surprised if you don''t get to see all the Las Vegas area attractions you''ve heard about   just plan another visit! Our warm, sunny climate lends itself to many popular activities, including year-round golf, tennis, hiking, boating, hot-air ballooning and much more.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (867, 'Ocean Gate Resort', 61, 'Come experience the historic charm of our country\'s oldest city, St. Augustine, Florida. The city itself celebrates over 400 years of history while the oldest hotels date back to the late 1800s, making this quaint seaside town one that is well-accustomed to visitors without losing its uniqueness. The prominent Spanish influences combined with southern hospitality in an ideal coastal climate make this a vacation destination desired by those of all ages and interests.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (868, 'King''s Land', 28, 'Kings'' Land resort is a 112 acre oasis carved out of an ancient lava field. Located directly between two of Hawaii''s finest golf courses, the Kings Course and the Beach Course, this resort features magnificent views of the Kings\' Course, Mauna Kea, and dazzling tropical sunsets over the Pacific Ocean.
INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (870, 'The Historic Powhatan Resort', 24, 'The land called ''Powhatan'' is a colonial plantation whose heritage dates to the 18th century. In 1735, Richard Taliaferro, the area''s leading architect, constructed Powhatan Plantation''s Manor House as his home. Restored with painstaking attention to historical detail, the manor house is the hallmark of our beautiful resort. The resort is located on over 250 acres of land, which includes a natural wildlife setting. Surrounded by a wooded forest, open fields, water foul and 5 small lakes, Powhatan offers a general store and two restaurants for our guest''s convenience including The Kitchen, an award winning fine dining restaurant. Amenities include a 19-hole mini-golf course, hiking and biking trails, basketball, volleyball, horseshoe pit, racquetball, tennis courts, weight room and a variety of workshops and classes for guests of all ages.ALL of our resorts will require a Major credit card ONLY deposit for all guests. No cash or debit cards allowed. The deposit will be $100 US and shall be returned when the rooms have been inspected and no damage has been done or items are not missing. If there is damage or items missing the guest will be charged.Please be advised that the indoor pool at The Historic Powhatan Resort is scheduled to be closed for repair starting on 9/29, and ends on 10/10. Guest can use the indoor pool at the sister property Greensprings Vacation Resort which is 5 minutes drive away.
