INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (876, 'Dreams Los Cabos', 5, 'Dreams Los Cabos is a luxurious, all suites resort that rests on the edge of the Baja Peninsula and presents a spectacular all inclusive package for both families and couples. More than 300 sumptuous suites rest just footsteps from the shoreline, and enticing amenities span throughout the resort. Indulge in fine a la carte cuisine or take advantage of numerous land sports. Supervised kids activities, 24-hour room service and exciting nighttime endeavors raise the all inclusive experience to a whole new level. Dreams Los Cabos also offers an impressive wedding and honeymoon package for budding couples. Bask in the pleasures at Dreams Los Cabos with a superior rate from All Inclusive Vacations!', '2009-06-08 11:25:36', '2009-06-08 11:25:36', 'dre1.jpg', 'image/jpeg', 25412, '2009-06-08 11:25:35', 'dre2.jpg', 'image/jpeg', 14860, '2009-06-08 11:25:34', 'dre3.jpg', 'image/jpeg', 39985, '2009-06-08 11:25:34', 'dre4.jpg', 'image/jpeg', 28360, '2009-06-08 11:25:35', 'dre5.jpg', 'image/jpeg', 16758, '2009-06-08 11:25:35');
