INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (879, 'Rangeley Lake Resort', 53, 'Courtesy and hospitality are the sweet toppings of this warm slice of  American pie called Rangeley.  The town may have grown, but there are still no traffic lights.  Or fast-food franchises with their gaudy neon signs.  Rangeley is still historic Rangeley to its very core.  It''s essence is clearly visible in the home-town feel of Scribner''s Hardware Store, River''s Edge Tackle Shop or the turn-of-the-century library.  Fast food is a Big Daddy Burger at Our Place Cafe, where the waitress delivers the weather forecast, the local fishing hotspots, as well as conditions on the snowmobile trails along side the juicy burger afloat in a sea of French fries.  Rangeley plays host to a wealth a shops ranging from antiques to clothes.  It also enjoys a wide range of restaurants and delis.', '2009-07-07 11:18:45', '2009-07-07 11:18:45', 'rang1.jpg', 'image/jpeg', 26055, '2009-07-07 11:18:45', 'rang2.jpg', 'image/jpeg', 19189, '2009-07-07 11:18:43', 'rang3.jpg', 'image/jpeg', 16825, '2009-07-07 11:18:44', 'rang4.JPG', 'image/jpeg', 88985, '2009-07-07 11:18:44', 'rang5.jpg', 'image/jpeg', 126187, '2009-07-07 11:18:44');
