INSERT INTO resorts (id, name, city_id, description, created_at, updated_at, photo_main_file_name, photo_main_content_type, photo_main_file_size, photo_main_updated_at, photo1_file_name, photo1_content_type, photo1_file_size, photo1_updated_at, photo2_file_name, photo2_content_type, photo2_file_size, photo2_updated_at, photo3_file_name, photo3_content_type, photo3_file_size, photo3_updated_at, photo4_file_name, photo4_content_type, photo4_file_size, photo4_updated_at) VALUES (889, 'Sandos Hotels & Resorts', 74, 'These all-inclusive resorts in Mexico boast a variety of restaurants and bars, tennis courts, pools, nightly entertainment and access to pristine stretches of sand. Take to the water for snorkeling, windsurfing or canoeing, or join one of the daily activities, which can include guided walks, dance classes, basketball, aerobics, and pool and beach games. These resorts are family-friendly -- the Sandos Kids Club has age-appropriate activities divided into four age groups: toddlers ages 2 and 3, children ages 4 to 7, youngsters ages 8 to 12, and teens ages 13 to 17.', '2009-09-15 16:37:49', '2009-09-15 16:37:49', 'sand1.jpg', 'image/jpeg', 19224, '2009-09-15 16:37:49', 'sand2.jpg', 'image/jpeg', 72705, '2009-09-15 16:37:47', 'sand3.jpg', 'image/jpeg', 45078, '2009-09-15 16:37:47', 'sand4.jpg', 'image/jpeg', 89941, '2009-09-15 16:37:48', 'sand5.jpg', 'image/jpeg', 33561, '2009-09-15 16:37:48');
